		if err != nil {
			select {
			case <-s.shutdown:
				return nil
			default:
				return err
			}
		}

		s.lock.Lock()
		s.conns[conn] = struct{}{}
		s.lock.Unlock()

		wg.Add(1)
		go s.serveTCPConn(&wg, conn)
	}
}

func (s *Server) serveTCPConn(wg *sync.WaitGroup, conn net.Conn) {
	defer wg.Done()
	defer func() {
		s.lock.Lock()
		delete(s.conns, conn)
		s.lock.Unlock()
		conn.Close()
	}()

	var reader io.Reader = conn
	if s.DecorateReader != nil {
		reader = s.DecorateReader(reader)
	}

	var writer io.Writer = conn
	if s.DecorateWriter != nil {
		writer = s.DecorateWriter(writer)
	}

	for {
		select {
		case <-s.shutdown:
			return
		default:
		}

		if s.ReadTimeout != 0 {
			conn.SetReadDeadline(time.Now().Add(s.ReadTimeout))
		}

		buf := make([]byte, s.UDPSize)
		n, err := reader.Read(buf)
		if err != nil {
			return
		}

		if s.MsgAcceptFunc != nil {
			if err := s.MsgAcceptFunc(buf[:n]); err != nil {
