	pass      [56]byte
	fallback  string
	proxy     proxy.Proxy
}

func (t *Trojan) ListenAndServe() {
	var ln net.Listener
	var err error

	if t.withTLS {
		cert, err := tls.LoadX509KeyPair(t.certFile, t.keyFile)
		if err != nil {
			return
		}
		t.tlsConfig = &tls.Config{Certificates: []tls.Certificate{cert}}
		ln, err = tls.Listen("tcp", t.addr, t.tlsConfig)
	} else {
		ln, err = net.Listen("tcp", t.addr)
	}

	if err != nil {
		return
	}
	defer ln.Close()

	for {
		conn, err := ln.Accept()
		if err != nil {
			continue
		}
		go t.Serve(conn)
	}
}

func (t *Trojan) Serve(c net.Conn) {
	defer c.Close()

	if t.withTLS {
		tlsConn := tls.Server(c, t.tlsConfig)
		if err := tlsConn.Handshake(); err != nil {
			return
		}
		c = tlsConn
	}

	buf := new(bytes.Buffer)
	cmd, target, err := t.readHeader(io.TeeReader(c, buf))
	if err != nil {
		return
	}

	switch cmd {
	case socks.CmdConnect:
		remote, err := t.proxy.Dial("tcp", target.String())
		if err != nil {
			if t.fallback != "" {
				t.serveFallback(c, t.fallback, buf)
			}
			return
		}
		defer remote.Close()

		if _, err := io.Copy(remote, buf); err != nil {
			return
		}

		go io.Copy(remote, c)
		io.Copy(c, remote)
	case socks.CmdUDP:
		t.ServeUoT(c, target)
	}
}

func (t *Trojan) readHeader(r io.Reader) (cmd socks.Cmd, target socks.Addr, err error) {
	var buf [56]byte
	if _, err := io.ReadFull(r, buf[:]); err != nil {
		return 0, nil, err
	}
	if buf != t.pass {
		return 0, nil, socks.ErrAuthFailed
	}

	cmd, target, err = socks.ReadAddr(r)
	if err != nil {
		return 0, nil, err
	}
	return cmd, target, nil
}

func (t *Trojan) serveFallback(c net.Conn, fallback string, buf *bytes.Buffer) {
	remote, err := net.Dial("tcp", fallback)
	if err != nil {
		return
	}
	defer remote.Close()

	if _, err := io.Copy(remote, buf); err != nil {
		return
	}

	go io.Copy(remote, c)
	io.Copy(c, remote)
