	Transport http.RoundTripper
}

func NewProxy() *Proxy {
	return &Proxy{
		Transport: &http.Transport{
			TLSClientConfig: &tls.Config{InsecureSkipVerify: true},
		},
	}
}

func (p *Proxy) RoundTrip(req *http.Request) (*http.Response, error) {
	return p.Transport.RoundTrip(req)
}

func (h *HTTP) ListenAndServe() error {
	ln, err := net.Listen("tcp", h.addr)
	if err != nil {
		return err
	}
	defer ln.Close()

	for {
		conn, err := ln.Accept()
		if err != nil {
			continue
		}
		go h.Serve(conn)
	}
}

func (h *HTTP) Serve(conn net.Conn) {
	defer conn.Close()
	conn.SetDeadline(time.Now().Add(30 * time.Second))

	reader := bufio.NewReader(conn)
	req, err := http.ReadRequest(reader)
	if err != nil {
		return
	}

	request := &request{
		Method:  req.Method,
		URL:     req.URL,
		Proto:   req.Proto,
		Header:  req.Header,
		Body:    req.Body,
		Conn:    conn,
		IsHTTPS: req.Method == "CONNECT",
	}

	if h.user != "" || h.password != "" {
		auth := req.Header.Get("Proxy-Authorization")
		if auth == "" {
			h.sendAuthRequired(conn)
			return
		}
		request.Auth = auth
	}

	proxyConn := &proxy.Conn{Conn: conn, Proxy: h.proxy}
	h.servRequest(request, proxyConn)
}

func (h *HTTP) servRequest(req *request, conn *proxy.Conn) {
	if !h.authenticate(req) {
		h.sendAuthRequired(req.Conn)
		return
	}

	if h.pretend {
		h.sendNotFound(req.Conn)
		return
	}

	if req.IsHTTPS {
		h.servHTTPS(req, req.Conn)
	} else {
		h.servHTTP(req, conn)
	}
}

func (h *HTTP) servHTTPS(req *request, conn net.Conn) {
	host := req.URL.Host
	if !strings.Contains(host, ":") {
		host += ":443"
	}

	targetConn, err := net.Dial("tcp", host)
	if err != nil {
		conn.Write([]byte("HTTP/1.1 502 Bad Gateway\r\n\r\n"))
		return
	}
	defer targetConn.Close()

	conn.Write([]byte("HTTP/1.1 200 Connection established\r\n\r\n"))

	go io.Copy(targetConn, conn)
	io.Copy(conn, targetConn)
}

func (h *HTTP) servHTTP(req *request, conn *proxy.Conn) {
	req.Header.Del("Proxy-Connection")
	req.Header.Del("Proxy-Authorization")

	urlStr := req.URL.String()
	if !strings.HasPrefix(urlStr, "http") {
		urlStr = "http://" + urlStr
	}

	newReq, err := http.NewRequest(req.Method, urlStr, req.Body)
	if err != nil {
		conn.Write([]byte("HTTP/1.1 500 Internal Server Error\r\n\r\n"))
		return
	}
	newReq.Header = req.Header

	resp, err := h.proxy.RoundTrip(newReq)
	if err != nil {
		conn.Write([]byte("HTTP/1.1 502 Bad Gateway\r\n\r\n"))
		return
	}
	defer resp.Body.Close()

	resp.Write(conn)
}

func (h *HTTP) authenticate(req *request) bool {
	if h.user == "" && h.password == "" {
		return true
	}

	auth := req.Auth
	if !strings.HasPrefix(auth, "Basic ") {
		return false
	}

	decoded, err := base64.StdEncoding.DecodeString(auth[6:])
	if err != nil {
		return false
	}

	parts := strings.SplitN(string(decoded), ":", 2)
	if len(parts) != 2 {
		return false
	}

	return parts[0] == h.user && parts[1] == h.password
}

func (h *HTTP) sendAuthRequired(conn net.Conn) {
	conn.Write([]byte("HTTP/1.1 407 Proxy Authentication Required\r\n"))
	conn.Write([]byte("Proxy-Authenticate: Basic realm=\"Proxy\"\r\n\r\n"))
}

func (h *HTTP) sendNotFound(conn net.Conn) {
	conn.Write([]byte("HTTP/1.1 404 Not Found\r\n"))
	conn.Write([]byte("Content-Type: text/plain\r\n"))
	conn.Write([]byte("\r\n"))
	conn.Write([]byte("404 Not Found\r\n"))
}

