package main

import (
	"context"
	"errors"
	"time"

	"google.golang.org/grpc"
	"google.golang.org/grpc/credentials"
	"google.golang.org/grpc/metadata"
)

type CloudSlackClient struct {
	log                Logger
	cfg                Config
	client             CloudSlackServiceClient
	commGroupMetadata  metadata.MD
	clusterName        string
	botID              string
}

type Logger interface {
	Errorf(format string, args ...interface{})
	Infof(format string, args ...interface{})
}

type Config struct {
	ServerAddress string
	TLSEnabled    bool
	TLSCertPath   string
}

type CloudSlackEvent struct {
	Type    string
	Payload []byte
}

type CloudSlackServiceClient interface {
	Connect(ctx context.Context, opts ...grpc.CallOption) (CloudSlack_ConnectClient, error)
}

type CloudSlack_ConnectClient interface {
	Send(*CloudSlackEvent) error
	Recv() (*CloudSlackEvent, error)
	grpc.ClientStream
}

func (c *CloudSlackClient) start(ctx context.Context) error {
	var opts []grpc.DialOption
	if c.cfg.TLSEnabled {
		creds, err := credentials.NewClientTLSFromFile(c.cfg.TLSCertPath, "")
		if err != nil {
			return err
		}
		opts = append(opts, grpc.WithTransportCredentials(creds))
	} else {
		opts = append(opts, grpc.WithInsecure())
	}

	conn, err := grpc.Dial(c.cfg.ServerAddress, opts...)
	if err != nil {
		return err
	}
	defer conn.Close()

	client := NewCloudSlackServiceClient(conn)
	streamCtx := metadata.NewOutgoingContext(ctx, c.commGroupMetadata)
	stream, err := client.Connect(streamCtx)
	if err != nil {
		return err
	}
	defer func() {
		if err := stream.CloseSend(); err != nil {
			c.log.Errorf("failed to close stream: %v", err)
		}
	}()

	if err := stream.Send(&CloudSlackEvent{
		Type:    "CONNECT",
		Payload: []byte(c.botID),
	}); err != nil {
		return err
	}

	eventChan := make(chan *CloudSlackEvent)
	errChan := make(chan error)

	go func() {
		for {
			event, err := stream.Recv()
			if err != nil {
				errChan <- err
				return
			}
			eventChan <- event
		}
	}()

	for {
		select {
		case <-ctx.Done():
			return nil
		case err := <-errChan:
			if errors.Is(err, context.Canceled) {
				return nil
			}
			return err
		case event := <-eventChan:
			if event.Type == "SHUTDOWN" {
				c.log.Infof("received shutdown signal from server")
				return nil
			}
			if err := c.handleEvent(event); err != nil {
				return err
			}
		case <-time.After(30 * time.Second):
			if err := stream.Send(&CloudSlackEvent{Type: "PING"}); err != nil {
				return err
			}
		}
	}
}

func (c *CloudSlackClient) handleEvent(event *CloudSlackEvent) error {
	return nil
}

