	"net"

	v3discoverygrpc "github.com/envoyproxy/go-control-plane/envoy/service/discovery/v3"
	v3lrsgrpc "github.com/envoyproxy/go-control-plane/envoy/service/load_stats/v3"
	"google.golang.org/grpc"
)

type Response struct {
	Resp interface{}
	Err  error
}

type Server struct {
	XDSRequestChan     *testutils.Channel
	XDSResponseChan    chan *Response
	LRSRequestChan     *testutils.Channel
	LRSResponseChan    chan *Response
	LRSStreamOpenChan  *testutils.Channel
	LRSStreamCloseChan *testutils.Channel
	NewConnChan        *testutils.Channel
	Address           string
	xdsServerV3       *xdsServerV3
	lrsServerV3       *lrsServerV3
}

type xdsServerV3 struct {
	v3discoverygrpc.UnimplementedAggregatedDiscoveryServiceServer
	server *Server
}

type lrsServerV3 struct {
	v3lrsgrpc.UnimplementedLoadReportingServiceServer
	server *Server
}

func NewServer(addr string) *Server {
	return &Server{
		XDSRequestChan:     testutils.NewChannel(),
		XDSResponseChan:    make(chan *Response),
		LRSRequestChan:     testutils.NewChannel(),
		LRSResponseChan:    make(chan *Response),
		LRSStreamOpenChan:  testutils.NewChannel(),
		LRSStreamCloseChan: testutils.NewChannel(),
		NewConnChan:        testutils.NewChannel(),
		Address:           addr,
		xdsServerV3:       &xdsServerV3{},
		lrsServerV3:       &lrsServerV3{},
	}
}

func (s *Server) StartServer(lis net.Listener) (*Server, func(), error) {
	grpcServer := grpc.NewServer()
	s.xdsServerV3.server = s
	s.lrsServerV3.server = s
	v3discoverygrpc.RegisterAggregatedDiscoveryServiceServer(grpcServer, s.xdsServerV3)
	v3lrsgrpc.RegisterLoadReportingServiceServer(grpcServer, s.lrsServerV3)

	go func() {
		grpcServer.Serve(lis)
	}()

	cleanup := func() {
		grpcServer.Stop()
	}
	return s, cleanup, nil
}

func (s *xdsServerV3) StreamAggregatedResources(stream v3discoverygrpc.AggregatedDiscoveryService_StreamAggregatedResourcesServer) error {
	s.server.NewConnChan.Send(nil)
	req, err := stream.Recv()
	if err != nil {
		return err
	}
	s.server.XDSRequestChan.Send(req)

	resp := <-s.server.XDSResponseChan
	if resp.Err != nil {
		return resp.Err
	}
	return stream.Send(resp.Resp.(*v3discoverygrpc.DiscoveryResponse))
}

func (s *lrsServerV3) StreamLoadStats(stream v3lrsgrpc.LoadReportingService_StreamLoadStatsServer) error {
	s.server.LRSStreamOpenChan.Send(nil)
	defer s.server.LRSStreamCloseChan.Send(nil)

	req, err := stream.Recv()
	if err != nil {
		return err
	}
	s.server.LRSRequestChan.Send(req)

	resp := <-s.server.LRSResponseChan
	if resp.Err != nil {
		return resp.Err
	}
	return stream.Send(resp.Resp.(*v3lrsgrpc.LoadStatsResponse))
}

