type ccSrv struct {
	l             net.Listener
	grpcSrv       *grpc.Server
	t             *testing.T
	expectedCCname string
}

func (s *ccSrv) Register(stream pb.ChaincodeSupport_RegisterServer) error {
	regMsg, err := stream.Recv()
	if err != nil {
		return err
	}

	if regMsg.GetRegister().GetChaincodeId() != s.expectedCCname {
		s.t.Fatalf("Expected chaincode name %s, got %s", s.expectedCCname, regMsg.GetRegister().GetChaincodeId())
	}

	for {
		msg, err := stream.Recv()
		if err != nil {
			return err
		}
		if err := stream.Send(msg); err != nil {
			return err
		}
	}
}

func (s *ccSrv) stop() {
	s.grpcSrv.Stop()
	s.l.Close()
}

func newCCServer(t *testing.T, port int, expectedCCname string, withTLS bool, ca tlsgen.CA) *ccSrv {
	var opts []grpc.ServerOption
	if withTLS {
		cert, err := tls.X509KeyPair(ca.CertBytes(), ca.KeyBytes())
		if err != nil {
			t.Fatalf("Failed to create server cert: %v", err)
		}
		tlsConfig := &tls.Config{
			Certificates: []tls.Certificate{cert},
			ClientAuth:   tls.NoClientCert,
		}
		opts = append(opts, grpc.Creds(credentials.NewTLS(tlsConfig)))
	}

	lis, err := net.Listen("tcp", net.JoinHostPort("", port))
	if err != nil {
		t.Fatalf("Failed to listen: %v", err)
	}

	srv := grpc.NewServer(opts...)
	ccSrv := &ccSrv{
		l:             lis,
		grpcSrv:       srv,
		t:             t,
		expectedCCname: expectedCCname,
	}
	pb.RegisterChaincodeSupportServer(srv, ccSrv)

	go func() {
		if err := srv.Serve(lis); err != nil {
			t.Logf("Server exited: %v", err)
		}
	}()

	return ccSrv
}

