package grpc

import (
	"context"
	"crypto/tls"
	"net"

	"github.com/opentracing/opentracing-go"
	"google.golang.org/grpc"
	"google.golang.org/grpc/credentials"
	"google.golang.org/grpc/reflection"
)

type Server struct {
	grpcServer *grpc.Server
	bufferSize int
}

func NewServer() *Server {
	return &Server{
		grpcServer: grpc.NewServer(),
		bufferSize: 1024,
	}
}

func (s *Server) Start(url *common.URL) error {
	lis, err := net.Listen("tcp", url.Location)
	if err != nil {
		return err
	}

	opts := []grpc.ServerOption{
		grpc.MaxRecvMsgSize(s.bufferSize),
		grpc.MaxSendMsgSize(s.bufferSize),
	}

	if url.TLSConfig != nil {
		creds := credentials.NewTLS(&tls.Config{
			Certificates: []tls.Certificate{*url.TLSConfig.Certificate},
			ClientAuth:   tls.NoClientCert,
		})
		opts = append(opts, grpc.Creds(creds))
	}

	if url.TracingEnabled {
		tracer := opentracing.GlobalTracer()
		opts = append(opts, grpc.UnaryInterceptor(otgrpc.OpenTracingServerInterceptor(tracer)))
		opts = append(opts, grpc.StreamInterceptor(otgrpc.OpenTracingStreamServerInterceptor(tracer)))
	}

	s.grpcServer = grpc.NewServer(opts...)

	for _, service := range url.Services {
		service.Register(s.grpcServer)
	}

	if url.ReflectionEnabled {
		reflection.Register(s.grpcServer)
	}

	go func() {
		if err := s.grpcServer.Serve(lis); err != nil {
			return
		}
	}()

	return nil
}

func (s *Server) Stop() {
	if s.grpcServer != nil {
		s.grpcServer.Stop()
	}
}

func (s *Server) GracefulStop() {
	if s.grpcServer != nil {
		s.grpcServer.GracefulStop()
	}
}

