func (p *Plumber) watchServiceShutdown(server *grpc.Server) {
	sigChan := make(chan os.Signal, 1)
	signal.Notify(sigChan, syscall.SIGINT, syscall.SIGTERM)

	select {
	case <-sigChan:
		p.log.Info("Received shutdown signal, stopping gRPC server gracefully")
		server.GracefulStop()
	case <-p.ServiceShutdownCtx.Done():
		p.log.Info("Service shutdown context cancelled, stopping gRPC server gracefully")
		server.GracefulStop()
	}
}

