package grpcserver

import (
	"context"
	"crypto/tls"
	"crypto/x509"
	"errors"
	"fmt"
	"net"
	"os"
	"time"

	"github.com/fsnotify/fsnotify"
	"github.com/rs/zerolog"
	"google.golang.org/grpc"
	"google.golang.org/grpc/credentials"
	"google.golang.org/grpc/keepalive"
)

type GRPCServerConfig struct {
	Address      string
	Network      string
	TLSCertPath  string
	TLSKeyPath   string
	MaxConnAge   time.Duration
	Enabled      bool
	BufferSize   int
	ClientCAPath string
	MaxWorkers   uint32
	flagPrefix   string
}

type RunnableGRPCServer interface {
	WithOpts(...grpc.ServerOption) RunnableGRPCServer
	Listen(context.Context) func() error
	DialContext(context.Context, ...grpc.DialOption) (*grpc.ClientConn, error)
	NetDialContext(context.Context, string) (net.Conn, error)
	Insecure() bool
	GracefulStop()
}

type completedGRPCServer struct {
	opts             []grpc.ServerOption
	listener         net.Listener
	svcRegistrationFn func(*grpc.Server)
	listenFunc      func() error
	prestopFunc     func()
	stopFunc        func()
	dial            func(context.Context, ...grpc.DialOption) (*grpc.ClientConn, error)
	netDial         func(context.Context, string) (net.Conn, error)
	creds           credentials.TransportCredentials
	certWatcher     *certwatcher.CertWatcher
}

func (c *completedGRPCServer) WithOpts(opts ...grpc.ServerOption) RunnableGRPCServer {
	c.opts = append(c.opts, opts...)
	return c
}

func (c *completedGRPCServer) Listen(ctx context.Context) func() error {
	return c.listenFunc
}

func (c *completedGRPCServer) DialContext(ctx context.Context, opts ...grpc.DialOption) (*grpc.ClientConn, error) {
	return c.dial(ctx, opts...)
}

func (c *completedGRPCServer) NetDialContext(ctx context.Context, addr string) (net.Conn, error) {
	return c.netDial(ctx, addr)
}

func (c *completedGRPCServer) Insecure() bool {
	return c.creds == nil
}

func (c *completedGRPCServer) GracefulStop() {
	if c.prestopFunc != nil {
		c.prestopFunc()
	}
	if c.stopFunc != nil {
		c.stopFunc()
	}
}

func (cfg *GRPCServerConfig) Complete(level zerolog.Level, svcRegistrationFn func(*grpc.Server), opts ...grpc.ServerOption) (RunnableGRPCServer, error) {
	if !cfg.Enabled {
		return nil, errors.New("server is disabled")
	}

	listener, dial, netDial, err := cfg.listenerAndDialer()
	if err != nil {
		return nil, fmt.Errorf("failed to create listener: %w", err)
	}

	tlsOpts, certWatcher, err := cfg.tlsOpts()
	if err != nil {
		return nil, fmt.Errorf("failed to configure TLS: %w", err)
	}
	opts = append(opts, tlsOpts...)

	creds, err := cfg.clientCreds()
	if err != nil {
		return nil, fmt.Errorf("failed to configure client credentials: %w", err)
	}

	server := grpc.NewServer(opts...)
	svcRegistrationFn(server)

	completed := &completedGRPCServer{
		opts:             opts,
		listener:         listener,
		svcRegistrationFn: svcRegistrationFn,
		dial:            dial,
		netDial:         netDial,
		creds:           creds,
		certWatcher:     certWatcher,
	}

	completed.listenFunc = func() error {
		return server.Serve(listener)
	}

	completed.stopFunc = func() {
		server.GracefulStop()
	}

	if certWatcher != nil {
		completed.prestopFunc = func() {
			certWatcher.Stop()
		}
	}

	return completed, nil
}

func (cfg *GRPCServerConfig) listenerAndDialer() (net.Listener, DialFunc, NetDialFunc, error) {
	switch cfg.Network {
	case "buffered":
		listener := newBufferedListener(cfg.BufferSize)
		dial := func(ctx context.Context, opts ...grpc.DialOption) (*grpc.ClientConn, error) {
			return grpc.DialContext(ctx, "buffered", append(opts, grpc.WithContextDialer(listener.DialContext))...)
		}
		netDial := func(ctx context.Context, addr string) (net.Conn, error) {
			return listener.DialContext(ctx, addr)
		}
		return listener, dial, netDial, nil
	default:
		listener, err := net.Listen(cfg.Network, cfg.Address)
		if err != nil {
			return nil, nil, nil, err
		}
		dial := func(ctx context.Context, opts ...grpc.DialOption) (*grpc.ClientConn, error) {
			return grpc.DialContext(ctx, cfg.Address, opts...)
		}
		netDial := func(ctx context.Context, addr string) (net.Conn, error) {
			var d net.Dialer
			return d.DialContext(ctx, cfg.Network, addr)
		}
		return listener, dial, netDial, nil
	}
}

func (cfg *GRPCServerConfig) tlsOpts() ([]grpc.ServerOption, *certwatcher.CertWatcher, error) {
	if cfg.TLSCertPath == "" || cfg.TLSKeyPath == "" {
		return nil, nil, nil
	}

	watcher, err := certwatcher.New(cfg.TLSCertPath, cfg.TLSKeyPath)
	if err != nil {
		return nil, nil, err
	}

	creds := credentials.NewTLS(&tls.Config{
		GetCertificate: watcher.GetCertificate,
		MinVersion:     tls.VersionTLS12,
	})

	opts := []grpc.ServerOption{
		grpc.Creds(creds),
		grpc.KeepaliveParams(keepalive.ServerParameters{
			MaxConnectionAge: cfg.MaxConnAge,
		}),
	}

	if cfg.MaxWorkers > 0 {
		opts = append(opts, grpc.NumStreamWorkers(cfg.MaxWorkers))
	}

	return opts, watcher, nil
}

func (cfg *GRPCServerConfig) clientCreds() (credentials.TransportCredentials, error) {
	if cfg.ClientCAPath == "" {
		return nil, nil
	}

	caCert, err := os.ReadFile(cfg.ClientCAPath)
	if err != nil {
		return nil, err
	}

	caCertPool := x509.NewCertPool()
	if !caCertPool.AppendCertsFromPEM(caCert) {
		return nil, errors.New("failed to add client CA certificate")
	}

	return credentials.NewTLS(&tls.Config{
		ClientCAs:  caCertPool,
		ClientAuth: tls.RequireAndVerifyClientCert,
	}), nil
}

type certwatcher struct {
	certFile    string
	keyFile     string
	certificate *tls.Certificate
	watcher     *fsnotify.Watcher
}

func New(certFile, keyFile string) (*certwatcher, error) {
	cert, err := tls.LoadX509KeyPair(certFile, keyFile)
	if err != nil {
		return nil, err
	}

	watcher, err := fsnotify.NewWatcher()
	if err != nil {
		return nil, err
	}

	cw := &certwatcher{
		certFile:    certFile,
		keyFile:     keyFile,
		certificate: &cert,
		watcher:     watcher,
	}

	if err := cw.watcher.Add(certFile); err != nil {
		return nil, err
	}
	if err := cw.watcher.Add(keyFile); err != nil {
		return nil, err
	}

	go cw.watch()

	return cw, nil
}

func (cw *certwatcher) GetCertificate(*tls.ClientHelloInfo) (*tls.Certificate, error) {
	return cw.certificate, nil
}

func (cw *certwatcher) watch() {
	for {
		select {
		case event, ok := <-cw.watcher.Events:
			if !ok {
				return
			}
			if event.Op&fsnotify.Write == fsnotify.Write {
				cert, err := tls.LoadX509KeyPair(cw.certFile, cw.keyFile)
				if err != nil {
					continue
				}
				cw.certificate = &cert
			}
		case err, ok := <-cw.watcher.Errors:
			if !ok {
				return
			}
			_ = err
		}
	}
}

func (cw *certwatcher) Stop() {
	cw.watcher.Close()
}

type bufferedListener struct {
	connChan chan net.Conn
	bufSize  int
}

func newBufferedListener(bufSize int) *bufferedListener {
	return &bufferedListener{
		connChan: make(chan net.Conn, bufSize),
		bufSize:  bufSize,
	}
}

func (bl *bufferedListener) Accept() (net.Conn, error) {
	conn, ok := <-bl.connChan
	if !ok {
		return nil, errors.New("listener closed")
	}
	return conn, nil
}

func (bl *bufferedListener) Close() error {
	close(bl.connChan)
	return nil
}

func (bl *bufferedListener) Addr() net.Addr {
	return &net.TCPAddr{IP: net.IPv4zero, Port: 0}
}

func (bl *bufferedListener) DialContext(ctx context.Context, addr string) (net.Conn, error) {
	serverConn, clientConn := net.Pipe()
	select {
	case bl.connChan <- serverConn:
		return clientConn, nil
	case <-ctx.Done():
		return nil, ctx.Err()
	}
}

