	"google.golang.org/grpc/keepalive"
)

type Config struct {
	GRPCServer      *grpc.Server
	Listener        string
	Endpoint        string
	ServerTLSConfig *tls.Config
}

func createListener(config Config) (net.Listener, error) {
	network, addr := parseListenerAddr(config.Listener)
	if network == "unix" {
		return net.Listen(network, addr)
	}
	return net.Listen(network, addr)
}

func grpcServer(config Config) (*grpc.Server, error) {
	var opts []grpc.ServerOption

	if config.ServerTLSConfig != nil {
		creds := credentials.NewTLS(config.ServerTLSConfig)
		opts = append(opts, grpc.Creds(creds))
	}

	keepaliveOpts := keepalive.ServerParameters{
		Time:    30 * time.Second,
		Timeout: 10 * time.Second,
	}
	opts = append(opts, grpc.KeepaliveParams(keepaliveOpts))

	if config.GRPCServer != nil {
		return config.GRPCServer, nil
	}

	return grpc.NewServer(opts...), nil
}

func parseListenerAddr(addr string) (string, string) {
	if strings.HasPrefix(addr, "unix://") {
		return "unix", strings.TrimPrefix(addr, "unix://")
	}
	if strings.HasPrefix(addr, "tcp://") {
		return "tcp", strings.TrimPrefix(addr, "tcp://")
	}
	return "tcp", addr
}

