		items: make(map[string]int32),
	}
	pb.RegisterIntStoreServer(s.gsrv, s)
	go func() {
		if err := s.gsrv.Serve(l); err != nil {
			fmt.Printf("Server error: %v\n", err)
		}
	}()
	return s, nil
}

func (s *intStoreServer) stop() {
	s.gsrv.GracefulStop()
}

func (s *intStoreServer) Set(ctx context.Context, item *pb.Item) (*pb.SetResponse, error) {
	value := s.setItem(item)
	return &pb.SetResponse{Value: value}, nil
}

func (s *intStoreServer) setItem(item *pb.Item) int32 {
	s.mu.Lock()
	defer s.mu.Unlock()
	s.items[item.Key] = item.Value
	return item.Value
}

func (s *intStoreServer) Get(ctx context.Context, req *pb.GetRequest) (*pb.Item, error) {
	s.mu.RLock()
	defer s.mu.RUnlock()
	value, ok := s.items[req.Key]
	if !ok {
		return nil, fmt.Errorf("key not found")
	}
	return &pb.Item{Key: req.Key, Value: value}, nil
}

func (s *intStoreServer) ListItems(req *pb.ListItemsRequest, stream pb.IntStore_ListItemsServer) error {
	s.mu.RLock()
	defer s.mu.RUnlock()
	for k, v := range s.items {
		if req.MinValue <= v && v <= req.MaxValue {
			if err := stream.Send(&pb.Item{Key: k, Value: v}); err != nil {
				return err
			}
		}
	}
	return nil
}

func (s *intStoreServer) SetStream(stream pb.IntStore_SetStreamServer) error {
	var total int32
	for {
		item, err := stream.Recv()
		if err != nil {
			return stream.SendAndClose(&pb.SetResponse{Value: total})
		}
		total += s.setItem(item)
	}
}

func (s *intStoreServer) StreamChat(stream pb.IntStore_StreamChatServer) error {
	for {
		item, err := stream.Recv()
		if err != nil {
			return err
		}
		s.mu.RLock()
		value, ok := s.items[item.Key]
		s.mu.RUnlock()
		if !ok {
			value = -1
		}
		if err := stream.Send(&pb.Item{Key: item.Key, Value: value}); err != nil {
			return err
		}
	}
}

