package rocketmq

import (
	"context"
	"errors"
	"sync"

	"github.com/apache/rocketmq-client-go/v2"
	"github.com/apache/rocketmq-client-go/v2/consumer"
	"github.com/apache/rocketmq-client-go/v2/primitive"
	"go.uber.org/ratelimit"
)

type PushConsumerConfig struct {
	NameServer      []string
	GroupName       string
	InstanceName    string
	Credentials     *primitive.Credentials
	ConsumerModel   consumer.MessageModel
	ConsumeFromWhere consumer.ConsumeFromWhere
	ConsumeOrderly bool
	RateLimitPerSec int
}

type PushConsumer struct {
	name             string
	rocketmq.PushConsumer
	PushConsumerConfig PushConsumerConfig
	subscribers       map[string]func(context.Context, ...*primitive.MessageExt) (consumer.ConsumeResult, error)
	interceptors     []primitive.Interceptor
	bucket           *ratelimit.Bucket
	started          bool
	mu               sync.Mutex
}

func NewPushConsumer(name string, config PushConsumerConfig) (*PushConsumer, error) {
	if len(config.NameServer) == 0 {
		return nil, errors.New("name servers cannot be empty")
	}

	opts := []consumer.Option{
		consumer.WithNameServer(config.NameServer),
		consumer.WithGroupName(config.GroupName),
		consumer.WithInstance(config.InstanceName),
		consumer.WithCredentials(config.Credentials),
		consumer.WithConsumerModel(config.ConsumerModel),
		consumer.WithConsumeFromWhere(config.ConsumeFromWhere),
	}

	if config.ConsumeOrderly {
		opts = append(opts, consumer.WithConsumerOrder(true))
	}

	rmqConsumer, err := rocketmq.NewPushConsumer(opts...)
	if err != nil {
		return nil, err
	}

	var bucket *ratelimit.Bucket
	if config.RateLimitPerSec > 0 {
		bucket = ratelimit.New(config.RateLimitPerSec)
	}

	return &PushConsumer{
		name:             name,
		PushConsumer:     rmqConsumer,
		PushConsumerConfig: config,
		subscribers:      make(map[string]func(context.Context, ...*primitive.MessageExt) (consumer.ConsumeResult, error)),
		bucket:           bucket,
	}, nil
}

func (c *PushConsumer) RegisterSingleMessage(f func(context.Context, *primitive.MessageExt) error) *PushConsumer {
	wrapped := func(ctx context.Context, msgs ...*primitive.MessageExt) (consumer.ConsumeResult, error) {
		if c.bucket != nil {
			c.bucket.Take()
		}

		for _, msg := range msgs {
			if err := f(ctx, msg); err != nil {
				return consumer.ConsumeRetryLater, err
			}
		}
		return consumer.ConsumeSuccess, nil
	}

	c.mu.Lock()
	defer c.mu.Unlock()
	for topic := range c.subscribers {
		c.subscribers[topic] = wrapped
	}
	return c
}

func (c *PushConsumer) Start() error {
	c.mu.Lock()
	defer c.mu.Unlock()

	if c.started {
		return nil
	}

	for topic, handler := range c.subscribers {
		if err := c.Subscribe(topic, "*", handler); err != nil {
			return err
		}
	}

	if len(c.interceptors) > 0 {
		c.RegisterInterceptor(c.interceptors...)
	}

	if err := c.PushConsumer.Start(); err != nil {
		return err
	}

	c.started = true
	return nil
}

