		opts = append(opts, producer.WithCredentials(*p.ProducerConfig.Credentials))
	}

	if p.ProducerConfig.Namespace != "" {
		opts = append(opts, producer.WithNamespace(p.ProducerConfig.Namespace))
	}

	if p.ProducerConfig.QueueSelector != nil {
		opts = append(opts, producer.WithQueueSelector(p.ProducerConfig.QueueSelector))
	}

	if p.ProducerConfig.Selector != nil {
		opts = append(opts, producer.WithSelector(p.ProducerConfig.Selector))
	}

	if p.ProducerConfig.DefaultTopic != "" {
		opts = append(opts, producer.WithDefaultTopicQueueNums(p.ProducerConfig.DefaultTopic))
	}

	if p.ProducerConfig.SendLatency {
		opts = append(opts, producer.WithTraceDispatcher())
	}

	if p.ProducerConfig.TraceDispatcher {
		opts = append(opts, producer.WithTraceDispatcher())
	}

	prod, err := rocketmq.NewProducer(opts...)
	if err != nil {
		return err
	}

	p.Producer = prod
	err = p.Producer.Start()
	if err != nil {
		return err
	}

	p.started = true
	return nil
}

func (p *Producer) Close() error {
	if !p.started {
		return nil
	}
	err := p.Producer.Shutdown()
	if err != nil {
		return err
	}
	p.started = false
	return nil
}

func (p *Producer) SendWithContext(ctx context.Context, msg []byte) error {
	if !p.started {
		return errors.New("producer not started")
	}
	m := primitive.NewMessage(p.ProducerConfig.DefaultTopic, msg)
	_, err := p.Producer.SendSync(ctx, m)
	return err
}

func (p *Producer) SendWithMsg(ctx context.Context, msg *primitive.Message) error {
	if !p.started {
		return errors.New("producer not started")
	}
	_, err := p.Producer.SendSync(ctx, msg)
	return err
}

func (p *Producer) SendWithResult(ctx context.Context, msg []byte, tag string) (*primitive.SendResult, error) {
	if !p.started {
		return nil, errors.New("producer not started")
	}
	m := primitive.NewMessage(p.ProducerConfig.DefaultTopic, msg)
	m.WithTag(tag)
	return p.Producer.SendSync(ctx, m)
}

func (p *Producer) SendMsg(ctx context.Context, msg *primitive.Message) (*primitive.SendResult, error) {
	if !p.started {
		return nil, errors.New("producer not started")
	}
	return p.Producer.SendSync(ctx, msg)
}

