			err := server.Shutdown(ctx)
			if err != nil {
				klog.ErrorS(err, "failed to shutdown server")
			}
			err = httpServer.Shutdown(ctx)
			if err != nil {
				klog.ErrorS(err, "failed to shutdown httpServer")
			}
