		go func() {
			err := server.ListenAndServeTLS("", "")
			if err != nil {
				klog.ErrorS(err, "failed to listen on master port")
			}
		}()
		go func() {
			err := httpServer.ListenAndServe()
			if err != nil {
				klog.ErrorS(err, "failed to listen on http master port")
			}
		}()
	}

