	sshConn, err := ssh.Dial("tcp", addr, config)
	if err != nil {
		log.Printf("Failed to dial SSH: %v", err)
		return
	}
	defer sshConn.Close()

	session, err := sshConn.NewSession()
	if err != nil {
		log.Printf("Failed to create SSH session: %v", err)
		return
	}
	defer session.Close()

	modes := ssh.TerminalModes{
		ssh.ECHO:          0,
		ssh.TTY_OP_ISPEED: 14400,
		ssh.TTY_OP_OSPEED: 14400,
	}

	if err := session.RequestPty("xterm", s.Height, s.Width, modes); err != nil {
		log.Printf("Failed to request PTY: %v", err)
		return
	}

	stdin, err := session.StdinPipe()
	if err != nil {
		log.Printf("Failed to setup stdin: %v", err)
		return
	}

	stdout, err := session.StdoutPipe()
	if err != nil {
		log.Printf("Failed to setup stdout: %v", err)
		return
	}

	stderr, err := session.StderrPipe()
	if err != nil {
		log.Printf("Failed to setup stderr: %v", err)
		return
	}

	if err := session.Start("/bin/bash"); err != nil {
		log.Printf("Failed to start shell: %v", err)
		return
	}

	done := make(chan bool, 1)

	go func() {
		defer func() { done <- true }()
		for {
			_, message, err := wsConn.ReadMessage()
			if err != nil {
				log.Printf("WebSocket read error: %v", err)
				return
			}
			if _, err := stdin.Write(message); err != nil {
				log.Printf("Stdin write error: %v", err)
				return
			}
		}
	}()

	go func() {
		defer func() { done <- true }()
		buf := make([]byte, 1024)
		for {
			n, err := stdout.Read(buf)
			if err != nil {
				log.Printf("Stdout read error: %v", err)
				return
			}
			if err := wsConn.WriteMessage(websocket.BinaryMessage, buf[:n]); err != nil {
				log.Printf("WebSocket write error: %v", err)
				return
			}
		}
	}()

	go func() {
		defer func() { done <- true }()
		buf := make([]byte, 1024)
		for {
			n, err := stderr.Read(buf)
			if err != nil {
				log.Printf("Stderr read error: %v", err)
				return
			}
			if err := wsConn.WriteMessage(websocket.BinaryMessage, buf[:n]); err != nil {
				log.Printf("WebSocket write error: %v", err)
				return
			}
		}
	}()

	<-done
	session.Close()
	sshConn.Close()
	wsConn.Close()
}

