package main

import (
	"context"
	"errors"
	"fmt"
	"io"
	"net/http"
	"sync"
	"time"

	"github.com/gin-gonic/gin"
	"github.com/gorilla/websocket"
	"go.uber.org/zap"
	"golang.org/x/crypto/ssh"
)

var upgrader = websocket.Upgrader{
	ReadBufferSize:  1024,
	WriteBufferSize: 1024,
	CheckOrigin: func(r *http.Request) bool {
		return true
	},
}

func ConnectSshPmExec(c *gin.Context, username, envName, productName, ip, hostId string, cols, rows int, log *zap.SugaredLogger) error {
	ws, err := upgrader.Upgrade(c.Writer, c.Request, nil)
	if err != nil {
		log.Errorf("Failed to upgrade websocket: %v", err)
		return err
	}
	defer ws.Close()

	sshConfig := &ssh.ClientConfig{
		User: username,
		Auth: []ssh.AuthMethod{
			ssh.Password(""),
		},
		HostKeyCallback: ssh.InsecureIgnoreHostKey(),
		Timeout:         30 * time.Second,
	}

	sshConn, err := ssh.Dial("tcp", ip+":22", sshConfig)
	if err != nil {
		log.Errorf("Failed to dial SSH: %v", err)
		return err
	}
	defer sshConn.Close()

	session, err := sshConn.NewSession()
	if err != nil {
		log.Errorf("Failed to create SSH session: %v", err)
		return err
	}
	defer session.Close()

	modes := ssh.TerminalModes{
		ssh.ECHO:          1,
		ssh.TTY_OP_ISPEED: 14400,
		ssh.TTY_OP_OSPEED: 14400,
	}

	if err := session.RequestPty("xterm", rows, cols, modes); err != nil {
		log.Errorf("Failed to request PTY: %v", err)
		return err
	}

	stdinPipe, err := session.StdinPipe()
	if err != nil {
		log.Errorf("Failed to get stdin pipe: %v", err)
		return err
	}

	stdoutPipe, err := session.StdoutPipe()
	if err != nil {
		log.Errorf("Failed to get stdout pipe: %v", err)
		return err
	}

	stderrPipe, err := session.StderrPipe()
	if err != nil {
		log.Errorf("Failed to get stderr pipe: %v", err)
		return err
	}

	if err := session.Shell(); err != nil {
		log.Errorf("Failed to start shell: %v", err)
		return err
	}

	ctx, cancel := context.WithCancel(context.Background())
	defer cancel()

	var wg sync.WaitGroup
	wg.Add(3)

	go func() {
		defer wg.Done()
		for {
			select {
			case <-ctx.Done():
				return
			default:
				_, msg, err := ws.ReadMessage()
				if err != nil {
					if !websocket.IsCloseError(err, websocket.CloseNormalClosure, websocket.CloseGoingAway) {
						log.Errorf("WebSocket read error: %v", err)
					}
					cancel()
					return
				}
				if _, err := stdinPipe.Write(msg); err != nil {
					log.Errorf("Failed to write to stdin: %v", err)
					cancel()
					return
				}
			}
		}
	}()

	go func() {
		defer wg.Done()
		buf := make([]byte, 1024)
		for {
			select {
			case <-ctx.Done():
				return
			default:
				n, err := stdoutPipe.Read(buf)
				if err != nil {
					if !errors.Is(err, io.EOF) {
						log.Errorf("Failed to read from stdout: %v", err)
					}
					cancel()
					return
				}
				if err := ws.WriteMessage(websocket.BinaryMessage, buf[:n]); err != nil {
					log.Errorf("Failed to write WebSocket message: %v", err)
					cancel()
					return
				}
			}
		}
	}()

	go func() {
		defer wg.Done()
		buf := make([]byte, 1024)
		for {
			select {
			case <-ctx.Done():
				return
			default:
				n, err := stderrPipe.Read(buf)
				if err != nil {
					if !errors.Is(err, io.EOF) {
						log.Errorf("Failed to read from stderr: %v", err)
					}
					cancel()
					return
				}
				if err := ws.WriteMessage(websocket.BinaryMessage, buf[:n]); err != nil {
					log.Errorf("Failed to write WebSocket message: %v", err)
					cancel()
					return
				}
			}
		}
	}()

	wg.Wait()
	return nil
}

