func WakeUp(macAddr, broadcastIP, bcastInterface string, port int) error {
	hwAddr, err := net.ParseMAC(macAddr)
	if err != nil {
		return err
	}

	var udpAddr *net.UDPAddr
	if bcastInterface != "" {
		iface, err := net.InterfaceByName(bcastInterface)
		if err != nil {
			return err
		}
		addrs, err := iface.Addrs()
		if err != nil {
			return err
		}
		if len(addrs) == 0 {
			return net.UnknownNetworkError("no address associated with interface")
		}
		ipnet, ok := addrs[0].(*net.IPNet)
		if !ok {
			return net.UnknownNetworkError("invalid address type for interface")
		}
		ip := ipnet.IP.To4()
		if ip == nil {
			return net.UnknownNetworkError("non-IPv4 address for interface")
		}
		udpAddr = &net.UDPAddr{
			IP:   ip,
			Port: 0,
		}
	}

	packet := buildMagicPacket(hwAddr)
	raddr := &net.UDPAddr{
		IP:   net.ParseIP(broadcastIP),
		Port: port,
	}

	conn, err := net.DialUDP("udp4", udpAddr, raddr)
	if err != nil {
		return err
	}
	defer conn.Close()

	_, err = conn.Write(packet)
	return err
}

func buildMagicPacket(mac net.HardwareAddr) []byte {
	var buf bytes.Buffer
	for i := 0; i < 6; i++ {
		buf.WriteByte(0xff)
	}
	for i := 0; i < 16; i++ {
		buf.Write(mac)
	}
	return buf.Bytes()
}

