type encryptedConn struct {
	conn   *net.UDPConn
	aead   cipher.AEAD
	nonce  []byte
	buffer []byte
}

func newEncryptedConn(conn *net.UDPConn, aead cipher.AEAD) *encryptedConn {
	return &encryptedConn{
		conn:   conn,
		aead:   aead,
		nonce:  make([]byte, aead.NonceSize()),
		buffer: make([]byte, 65536),
	}
}

func (ec *encryptedConn) read() ([]byte, *net.UDPAddr, error) {
	n, addr, err := ec.conn.ReadFromUDP(ec.buffer)
	if err != nil {
		return nil, nil, err
	}

	if _, err := rand.Read(ec.nonce); err != nil {
		return nil, nil, err
	}

	plaintext, err := ec.aead.Open(nil, ec.nonce, ec.buffer[:n], nil)
	if err != nil {
		return nil, nil, err
	}

	return plaintext, addr, nil
}

func (ec *encryptedConn) write(data []byte, addr *net.UDPAddr) error {
	if _, err := rand.Read(ec.nonce); err != nil {
		return err
	}

	ciphertext := ec.aead.Seal(nil, ec.nonce, data, nil)
	_, err := ec.conn.WriteToUDP(ciphertext, addr)
	return err
}

func remote2remoteUDP(remoteA, remoteB string, raenc, rbenc bool) error {
	addrA, err := net.ResolveUDPAddr("udp", remoteA)
	if err != nil {
		return err
	}

	addrB, err := net.ResolveUDPAddr("udp", remoteB)
	if err != nil {
		return err
	}

	connA, err := net.DialUDP("udp", nil, addrA)
	if err != nil {
		return err
	}
	defer connA.Close()

	connB, err := net.DialUDP("udp", nil, addrB)
	if err != nil {
		return err
	}
	defer connB.Close()

	var aeadA, aeadB cipher.AEAD
	if raenc {
		key := make([]byte, 32)
		if _, err := rand.Read(key); err != nil {
			return err
		}
		aeadA, err = newAESGCM(key)
		if err != nil {
			return err
		}
	}

	if rbenc {
		key := make([]byte, 32)
		if _, err := rand.Read(key); err != nil {
			return err
		}
		aeadB, err = newAESGCM(key)
		if err != nil {
			return err
		}
	}

	var ecA, ecB *encryptedConn
	if raenc {
		ecA = newEncryptedConn(connA, aeadA)
	}
	if rbenc {
		ecB = newEncryptedConn(connB, aeadB)
	}

	var wg sync.WaitGroup
	wg.Add(2)

	go func() {
		defer wg.Done()
		forward(connA, ecA, connB, ecB)
	}()

	go func() {
		defer wg.Done()
		forward(connB, ecB, connA, ecA)
	}()

	wg.Wait()
	return nil
}

func forward(src *net.UDPConn, srcEnc *encryptedConn, dst *net.UDPConn, dstEnc *encryptedConn) {
	buf := make([]byte, 65536)
	for {
		var data []byte
		var addr *net.UDPAddr
		var err error

		if srcEnc != nil {
			data, addr, err = srcEnc.read()
		} else {
			n, a, err := src.ReadFromUDP(buf)
			if err != nil {
				return
			}
			data, addr = buf[:n], a
		}

		if err != nil {
			return
		}

		if dstEnc != nil {
			err = dstEnc.write(data, addr)
		} else {
			_, err = dst.WriteToUDP(data, addr)
		}

		if err != nil {
			return
		}
	}
}

func newAESGCM(key []byte) (cipher.AEAD, error) {
	block, err := aes.NewCipher(key)
	if err != nil {
		return nil, err
	}
	return cipher.NewGCM(block)
}

