type TrapHandlerFunc func(*SnmpPacket, *net.UDPAddr)

type TrapListener struct {
	done                            chan bool
	listening                       chan bool
	sync.Mutex
	Params                          *GoSNMP
	OnNewTrap                       TrapHandlerFunc
	CloseTimeout                    time.Duration
	conn                            *net.UDPConn
	proto                           string
	usmStatsUnknownEngineIDsCount   uint32
	finish                          int32
}

func NewTrapListener() *TrapListener {
	return &TrapListener{
		done:         make(chan bool),
		listening:    make(chan bool),
		Params:       Default,
		CloseTimeout: 5 * time.Second,
		proto:        "udp",
	}
}

func (t *TrapListener) Close() {
	if atomic.CompareAndSwapInt32(&t.finish, 0, 1) {
		close(t.done)
		if t.conn != nil {
			t.conn.SetReadDeadline(time.Now())
		}
		select {
		case <-t.listening:
		case <-time.After(t.CloseTimeout):
		}
		if t.conn != nil {
			t.conn.Close()
		}
	}
}

func (t *TrapListener) SendUDP(packet *SnmpPacket, addr *net.UDPAddr) error {
	if t.conn == nil {
		return errors.New("connection not established")
	}
	_, err := t.conn.WriteToUDP(packet.marshalMsg(), addr)
	return err
}

func (t *TrapListener) listenUDP(addr string) error {
	udpAddr, err := net.ResolveUDPAddr(t.proto, addr)
	if err != nil {
		return err
	}

	t.Lock()
	t.conn, err = net.ListenUDP(t.proto, udpAddr)
	t.Unlock()
	if err != nil {
		return err
	}

	defer t.Close()
	close(t.listening)

	buf := make([]byte, 65536)
	for {
		select {
		case <-t.done:
			return nil
		default:
			t.conn.SetReadDeadline(time.Now().Add(time.Second))
			n, remoteAddr, err := t.conn.ReadFromUDP(buf)
			if err != nil {
				if netErr, ok := err.(net.Error); ok && netErr.Timeout() {
					continue
				}
				return err
			}

			packet, err := t.Params.unmarshalTrap(buf[:n])
			if err != nil {
				continue
			}

			if t.OnNewTrap != nil {
				go t.OnNewTrap(packet, remoteAddr)
			}
		}
	}
}

func (t *TrapListener) reportAuthoritativeEngineID(trap *SnmpPacket, snmpEngineID string, addr *net.UDPAddr) error {
	if trap.Version != Version3 {
		return nil
	}

	if trap.SecurityParameters == nil {
		return errors.New("missing security parameters")
	}

	usm, ok := trap.SecurityParameters.(*UsmSecurityParameters)
	if !ok {
		return errors.New("invalid security parameters type")
	}

	if usm.AuthoritativeEngineID == snmpEngineID {
		return nil
	}

	atomic.AddUint32(&t.usmStatsUnknownEngineIDsCount, 1)

	reportPacket := &SnmpPacket{
		Version:            Version3,
		MsgFlags:           Reportable,
		SecurityModel:      UserSecurityModel,
		SecurityParameters: t.Params.SecurityParameters,
		ContextEngineID:    snmpEngineID,
		ContextName:        "",
		PDUType:           Report,
		MsgID:             trap.MsgID,
		RequestID:         trap.RequestID,
		Error:             NoError,
		ErrorIndex:        0,
		Variables: []SnmpPDU{
			{
				Name:  usmUnknownEngineIDs,
				Type:  Counter32,
				Value: t.usmStatsUnknownEngineIDsCount,
			},
		},
	}

	return t.SendUDP(reportPacket, addr)
}

