	}

	udpConn, err := net.ListenUDP("udp", nil)
	if err != nil {
		conn.Close()
		return
	}

	hyUDP, err := s.HyClient.UDP()
	if err != nil {
		udpConn.Close()
		conn.Close()
		return
	}

	err = s.sendUDPReply(conn, udpConn.LocalAddr().(*net.UDPAddr))
	if err != nil {
		hyUDP.Close()
		udpConn.Close()
		conn.Close()
		return
	}

	go s.udpServer(udpConn, hyUDP)
}

func (s *Server) udpServer(udpConn *net.UDPConn, hyUDP client.HyUDPConn) error {
	defer udpConn.Close()
	defer hyUDP.Close()

	buf := make([]byte, client.MaxUDPSize)
	for {
		n, addr, err := udpConn.ReadFromUDP(buf)
		if err != nil {
			if s.EventLogger != nil {
				s.EventLogger.UDPError(addr, err)
			}
			if errors.Is(err, net.ErrClosed) {
				return nil
			}
			continue
		}

		if s.EventLogger != nil {
			s.EventLogger.UDPRequest(addr)
		}

