		copy(data, buf[:n])

		go func(addr *net.UDPAddr, data []byte) {
			resp, err := hyUDP.Request(data)
			if err != nil {
				if s.EventLogger != nil {
					s.EventLogger.UDPError(addr, err)
				}
				return
			}

			_, err = udpConn.WriteToUDP(resp, addr)
			if err != nil && s.EventLogger != nil {
				s.EventLogger.UDPError(addr, err)
			}
		}(addr, data)
	}
}

func (s *Server) sendUDPReply(conn net.Conn, addr *net.UDPAddr) error {
	reply := socks5.NewReply(socks5.RepSuccess, addr)
	_, err := reply.WriteTo(conn)
	if err != nil {
		return err
	}

	go func() {
		time.Sleep(10 * time.Second)
		conn.Close()
	}()

	return nil
}

