package main

import (
	"bytes"
	"context"
	"crypto/rand"
	"encoding/json"
	"errors"
	"fmt"
	"io"
	"log"
	"net/http"
	"sync"
	"time"

	"github.com/gorilla/websocket"
	"github.com/pion/webrtc/v3"
)

type onDataChannelOpen func(*webrtc.DataChannel)

type outboundOfferValue struct {
	pc       *wrappedPeerConnection
	onOpened onDataChannelOpen
}

type wrappedPeerConnection struct {
	*webrtc.PeerConnection
	mu sync.Mutex
}

type offerContext struct {
	InfoHash [20]byte `json:"info_hash"`
	PeerId   [20]byte `json:"peer_id"`
}

type TrackerClient struct {
	Url                         string
	GetAnnounceRequest          func(_ tracker.AnnounceEvent, infoHash [20]byte) (tracker.AnnounceRequest, error)
	PeerId                      [20]byte
	OnConn                      onDataChannelOpen
	Logger                      log.Logger
	Dialer                      *websocket.Dialer
	mu                          sync.Mutex
	cond                        *sync.Cond
	outboundOffers              map[string]outboundOfferValue
	wsConn                      *websocket.Conn
	closed                      bool
	pingTicker                  *time.Ticker
	WebsocketTrackerHttpHeader  func() http.Header
	ICEServers                  []webrtc.ICEServer
	rtcPeerConns                map[string]*wrappedPeerConnection
}

func (c *TrackerClient) doWebsocket() error {
	c.mu.Lock()
	defer c.mu.Unlock()

	if c.closed {
		return errors.New("client closed")
	}

	wsConn, _, err := c.Dialer.Dial(c.Url, c.WebsocketTrackerHttpHeader())
	if err != nil {
		return err
	}
	c.wsConn = wsConn

	c.pingTicker = time.NewTicker(30 * time.Second)
	go func() {
		for range c.pingTicker.C {
			c.writeMessage([]byte(`{"type":"ping"}`))
		}
	}()

	go c.trackerReadLoop(wsConn)

	return nil
}

func (c *TrackerClient) trackerReadLoop(wsConn *websocket.Conn) error {
	defer wsConn.Close()

	for {
		_, msg, err := wsConn.ReadMessage()
		if err != nil {
			c.mu.Lock()
			if !c.closed {
				go c.doWebsocket()
			}
			c.mu.Unlock()
			return err
		}

		var baseMsg struct {
			Type string `json:"type"`
		}
		if err := json.Unmarshal(msg, &baseMsg); err != nil {
			c.Logger.Printf("error unmarshaling message: %v", err)
			continue
		}

		switch baseMsg.Type {
		case "offer":
			var offerMsg struct {
				Offer string       `json:"offer"`
				From  string       `json:"from"`
				OfferCtx offerContext `json:"offer_ctx"`
			}
			if err := json.Unmarshal(msg, &offerMsg); err != nil {
				c.Logger.Printf("error unmarshaling offer: %v", err)
				continue
			}
			if err := c.handleOffer(offerMsg.OfferCtx, offerMsg.Offer); err != nil {
				c.Logger.Printf("error handling offer: %v", err)
				continue
			}
		case "answer":
			var answerMsg struct {
				Answer string `json:"answer"`
				To     string `json:"to"`
			}
			if err := json.Unmarshal(msg, &answerMsg); err != nil {
				c.Logger.Printf("error unmarshaling answer: %v", err)
				continue
			}
			c.handleAnswer(answerMsg.To, webrtc.SessionDescription{
				Type: webrtc.SDPTypeAnswer,
				SDP:  answerMsg.Answer,
			})
		default:
			c.Logger.Printf("unknown message type: %s", baseMsg.Type)
		}
	}
}

func (c *TrackerClient) writeMessage(msg []byte) error {
	c.mu.Lock()
	defer c.mu.Unlock()

	if c.closed || c.wsConn == nil {
		return errors.New("connection closed")
	}

	return c.wsConn.WriteMessage(websocket.TextMessage, msg)
}

func (c *TrackerClient) handleOffer(offerCtx offerContext, offerStr string) error {
	offer := webrtc.SessionDescription{
		Type: webrtc.SDPTypeOffer,
		SDP:  offerStr,
	}

	config := webrtc.Configuration{
		ICEServers: c.ICEServers,
	}

	pc, err := webrtc.NewPeerConnection(config)
	if err != nil {
		return err
	}

	wrappedPC := &wrappedPeerConnection{PeerConnection: pc}
	c.mu.Lock()
	c.rtcPeerConns[fmt.Sprintf("%x", offerCtx.PeerId)] = wrappedPC
	c.mu.Unlock()

	pc.OnDataChannel(func(d *webrtc.DataChannel) {
		d.OnOpen(func() {
			c.OnConn(d)
		})
	})

	if err := pc.SetRemoteDescription(offer); err != nil {
		return err
	}

	answer, err := pc.CreateAnswer(nil)
	if err != nil {
		return err
	}

	if err := pc.SetLocalDescription(answer); err != nil {
		return err
	}

	answerMsg := struct {
		Type   string `json:"type"`
		To     string `json:"to"`
		Answer string `json:"answer"`
	}{
		Type:   "answer",
		To:     fmt.Sprintf("%x", offerCtx.PeerId),
		Answer: answer.SDP,
	}

	answerBytes, err := json.Marshal(answerMsg)
	if err != nil {
		return err
	}

	return c.writeMessage(answerBytes)
}

func (c *TrackerClient) handleAnswer(to string, answer webrtc.SessionDescription) {
	c.mu.Lock()
	defer c.mu.Unlock()

	offer, ok := c.outboundOffers[to]
	if !ok {
		c.Logger.Printf("no outbound offer for peer %s", to)
		return
	}

	if err := offer.pc.SetRemoteDescription(answer); err != nil {
		c.Logger.Printf("error setting remote description: %v", err)
		return
	}

	delete(c.outboundOffers, to)
}

