type WebsocketProxy struct {
	Director func(*http.Request, http.Header)
	Backend  func(*http.Request) *url.URL
	Upgrader *websocket.Upgrader
	Dialer   *websocket.Dialer
}

func (p *WebsocketProxy) ServeHTTP(w http.ResponseWriter, r *http.Request) {
	if p.Backend == nil {
		http.Error(w, "backend function not configured", http.StatusInternalServerError)
		return
	}

	backendURL := p.Backend(r)
	if backendURL == nil {
		http.Error(w, "backend URL not found", http.StatusBadRequest)
		return
	}

	backendHeaders := http.Header{}
	if p.Director != nil {
		p.Director(r, backendHeaders)
	}

	backendConn, _, err := p.Dialer.Dial(backendURL.String(), backendHeaders)
	if err != nil {
		http.Error(w, "backend connection failed", http.StatusBadGateway)
		return
	}
	defer backendConn.Close()

	clientConn, err := p.Upgrader.Upgrade(w, r, nil)
	if err != nil {
		http.Error(w, "client upgrade failed", http.StatusInternalServerError)
		return
	}
	defer clientConn.Close()

	errChan := make(chan error, 2)
	go p.replicateWebsocketConn(clientConn, backendConn, errChan)
	go p.replicateWebsocketConn(backendConn, clientConn, errChan)

	<-errChan
}

func (p *WebsocketProxy) replicateWebsocketConn(src, dst *websocket.Conn, errChan chan error) {
	for {
		msgType, msg, err := src.ReadMessage()
		if err != nil {
			errChan <- err
			return
		}

		err = dst.WriteMessage(msgType, msg)
		if err != nil {
			errChan <- err
			return
		}
	}
}

