type testServer struct {
	sync.Mutex
	eventEmitter        *eventEmitter
	mux                 *http.ServeMux
	testServer          *httptest.Server
	routes              map[string]http.HandlerFunc
	requestSubscribers  map[string][]chan *http.Request
	PREFIX              string
	EMPTY_PAGE          string
	CROSS_PROCESS_PREFIX string
	WS_PREFIX           string
	PORT                string
	wsConnections       []*wsConnection
	wsConnectionChan    chan *wsConnection
	wsMessageHandlers   []func(*websocket.Conn, *http.Request, websocket.MessageType, []byte)
	wsCloseHandlers     []func(*websocket.CloseError)
}

func (s *testServer) wsHandler(w http.ResponseWriter, r *http.Request) {
	c, err := websocket.Accept(w, r, &websocket.AcceptOptions{})
	if err != nil {
		return
	}
	defer c.Close(websocket.StatusInternalError, "connection closed")

	wsc := &wsConnection{Conn: c, Req: r}
	s.Lock()
	s.wsConnections = append(s.wsConnections, wsc)
	if s.wsConnectionChan != nil {
		s.wsConnectionChan <- wsc
		s.wsConnectionChan = nil
	}
	s.Unlock()

	for {
		msgType, msg, err := c.Read(context.Background())
		if err != nil {
			if closeErr, ok := err.(*websocket.CloseError); ok {
				s.Lock()
