		}

		s.Lock()
		handlers := make([]func(*websocket.Conn, *http.Request, websocket.MessageType, []byte), len(s.wsMessageHandlers))
		copy(handlers, s.wsMessageHandlers)
		s.Unlock()

		for _, handler := range handlers {
			handler(c, r, msgType, msg)
		}
	}
}

func (s *testServer) OnceWebSocketConnection(handler func(c *websocket.Conn, r *http.Request)) {
	s.Lock()
	defer s.Unlock()
	if len(s.wsConnections) > 0 {
		handler(s.wsConnections[0].Conn, s.wsConnections[0].Req)
		return
	}
	s.wsConnectionChan = make(chan *wsConnection, 1)
	go func() {
		wsc := <-s.wsConnectionChan
		handler(wsc.Conn, wsc.Req)
	}()
}

func (s *testServer) OnWebSocketClose(handler func(err *websocket.CloseError)) {
	s.Lock()
	defer s.Unlock()
	s.wsCloseHandlers = append(s.wsCloseHandlers, handler)
}

func (s *testServer) OnceWebSocketClose(handler func(err *websocket.CloseError)) {
	var once sync.Once
	wrappedHandler := func(err *websocket.CloseError) {
		once.Do(func() {
			handler(err)
		})
	}
	s.OnWebSocketClose(wrappedHandler)
}

func (s *testServer) OnWebSocketMessage(handler func(c *websocket.Conn, r *http.Request, msgType websocket.MessageType, msg []byte)) {
	s.Lock()
	defer s.Unlock()
	s.wsMessageHandlers = append(s.wsMessageHandlers, handler)
}

func (s *testServer) OnceWebSocketMessage(handler func(c *websocket.Conn, r *http.Request, msgType websocket.MessageType, msg []byte)) {
	var once sync.Once
	wrappedHandler := func(c *websocket.Conn, r *http.Request, msgType websocket.MessageType, msg []byte) {
		once.Do(func() {
			handler(c, r, msgType, msg)
		})
	}
	s.OnWebSocketMessage(wrappedHandler)
}

func (s *testServer) SendOnWebSocketConnection(msgType websocket.MessageType, data []byte) {
	s.Lock()
	conns := make([]*wsConnection, len(s.wsConnections))
	copy(conns, s.wsConnections)
	s.Unlock()

	for _, conn := range conns {
		conn.SendMessage(msgType, data)
	}
}

func (s *testServer) WaitForWebSocketConnection() <-chan *wsConnection {
	ch := make(chan *wsConnection, 1)
	s.Lock()
	if len(s.wsConnections) > 0 {
		ch <- s.wsConnections[0]
		close(ch)
	} else {
		s.wsConnectionChan = ch
	}
	s.Unlock()
	return ch
}

type eventEmitter struct {
	listeners map[string][]func(interface{})
	mu        sync.Mutex
}

func (e *eventEmitter) On(event string, handler func(interface{})) {
	e.mu.Lock()
	defer e.mu.Unlock()
	if e.listeners == nil {
		e.listeners = make(map[string][]func(interface{}))
	}
	e.listeners[event] = append(e.listeners[event], handler)
}

func (e *eventEmitter) Emit(event string, data interface{}) {
	e.mu.Lock()
	defer e.mu.Unlock()
	if handlers, ok := e.listeners[event]; ok {
		for _, handler := range handlers {
			go handler(data)
		}
	}
}

