package main

import (
	"io"
	"os"
	"sync"

	"github.com/containerd/cio"
)

type fileCloner struct {
	file   *os.File
	writer *os.File
	mu     sync.Mutex
}

func (fc *fileCloner) Write(p []byte) (int, error) {
	fc.mu.Lock()
	defer fc.mu.Unlock()
	if _, err := fc.file.Write(p); err != nil {
		return 0, err
	}
	return fc.writer.Write(p)
}

func (fc *fileCloner) Close() error {
	fc.mu.Lock()
	defer fc.mu.Unlock()
	if err := fc.file.Close(); err != nil {
		return err
	}
	return fc.writer.Close()
}

type logRetriever struct {
	reader *os.File
	writer *os.File
	output *fileCloner
}

func newlogRetriever(logFile string) (*logRetriever, error) {
	r, w, err := os.Pipe()
	if err != nil {
		return nil, err
	}

	file, err := os.OpenFile(logFile, os.O_APPEND|os.O_CREATE|os.O_WRONLY, 0644)
	if err != nil {
		return nil, err
	}

	output := &fileCloner{
		file:   file,
		writer: w,
	}

	if _, err := os.Stat(logFile); err == nil {
		src, err := os.Open(logFile)
		if err != nil {
			return nil, err
		}
		defer src.Close()
		if _, err := io.Copy(w, src); err != nil {
			return nil, err
		}
	}

	return &logRetriever{
		reader: r,
		writer: w,
		output: output,
	}, nil
}

func (lr *logRetriever) Read(p []byte) (int, error) {
	return lr.reader.Read(p)
}

func (lr *logRetriever) CloseWriter() error {
	return lr.writer.Close()
}

func (lr *logRetriever) Close() error {
	if err := lr.reader.Close(); err != nil {
		return err
	}
	return lr.output.Close()
}

func (lr *logRetriever) Opt() cio.Opt {
	return cio.WithStreams(cio.NullReader, lr.output, lr.output)
}

