	time.Sleep(10 * time.Millisecond)
	writer := io.MultiWriter(w, os.Stdout)
	io.WriteString(writer, "Method: "+req.Method+"\n")
	io.WriteString(writer, "URL: "+req.URL.String()+"\n")
	io.WriteString(writer, "Headers:\n")
