package main

import (
	"encoding/json"
	"html/template"
	"net/http"
	"strings"

	"github.com/ossf/scorecard/v4/clients/githubrepo"
	"github.com/ossf/scorecard/v4/pkg"
	"github.com/spf13/cobra"
)

type server struct {
	templates *template.Template
	options   *options.Options
}

func newServer(o *options.Options) (*server, error) {
	tmpl, err := template.ParseGlob("templates/*.html")
	if err != nil {
		return nil, err
	}
	return &server{
		templates: tmpl,
		options:   o,
	}, nil
}

func (s *server) handleScorecard(w http.ResponseWriter, r *http.Request) {
	repoURL := r.URL.Query().Get("repo")
	if repoURL == "" {
		http.Error(w, "missing repo parameter", http.StatusBadRequest)
		return
	}

	repo, err := githubrepo.MakeGithubRepo(repoURL)
	if err != nil {
		http.Error(w, err.Error(), http.StatusBadRequest)
		return
	}

	ctx := r.Context()
	repoClient := githubrepo.CreateGithubRepoClient(ctx)
	defer repoClient.Close()

	checkRequest := pkg.CheckRequest{
		Ctx:        ctx,
		RepoClient: repoClient,
		Repo:       repo,
	}

	results, err := pkg.RunScorecards(ctx, checkRequest)
	if err != nil {
		http.Error(w, err.Error(), http.StatusInternalServerError)
		return
	}

	accept := r.Header.Get("Accept")
	if strings.Contains(accept, "application/json") {
		w.Header().Set("Content-Type", "application/json")
		if err := json.NewEncoder(w).Encode(results); err != nil {
			http.Error(w, err.Error(), http.StatusInternalServerError)
		}
		return
	}

	w.Header().Set("Content-Type", "text/html")
	if err := s.templates.ExecuteTemplate(w, "results.html", results); err != nil {
		http.Error(w, err.Error(), http.StatusInternalServerError)
	}
}

func serveCmd(o *options.Options) *cobra.Command {
	return &cobra.Command{
		Use:   "serve",
		Short: "Start HTTP server for scorecard checks",
		Run: func(cmd *cobra.Command, args []string) {
			srv, err := newServer(o)
			if err != nil {
				panic(err)
			}

			http.HandleFunc("/scorecard", srv.handleScorecard)
			if err := http.ListenAndServe(":8080", nil); err != nil {
				panic(err)
			}
		},
	}
}

