package main

import (
	"html/template"
	"net/http"
	"os"
	"path/filepath"
)

type Example struct {
	Type string
	Link string
	Name string
	Desc string
}

func getExamples() []Example {
	return []Example{
		{"basic", "hello", "Hello World", "A simple hello world example"},
		{"basic", "values", "Values", "Basic value types and operations"},
	}
}

func serve(addr string) error {
	examples := getExamples()

	tmpl := template.Must(template.New("").Parse(`
<!DOCTYPE html>
<html>
<head>
	<title>{{.Title}}</title>
</head>
<body>
	<h1>{{.Title}}</h1>
	{{range .Examples}}
	<div>
		<h2><a href="/example/{{.Type}}/{{.Link}}">{{.Name}}</a></h2>
		<p>{{.Desc}}</p>
	</div>
	{{end}}
</body>
</html>
`))

	http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
		if r.URL.Path != "/" {
			http.NotFound(w, r)
			return
		}
		tmpl.Execute(w, map[string]interface{}{
			"Title":    "Go Examples",
			"Examples": examples,
		})
	})

	http.HandleFunc("/wasm_exec.js", func(w http.ResponseWriter, r *http.Request) {
		w.Header().Set("Content-Type", "application/javascript")
		http.ServeFile(w, r, filepath.Join(os.Getenv("GOROOT"), "misc/wasm/wasm_exec.js"))
	})

	http.HandleFunc("/example/", func(w http.ResponseWriter, r *http.Request) {
		path := r.URL.Path[len("/example/"):]
		parts := make([]string, 0)
		for _, p := range filepath.SplitList(path) {
			if p != "" {
				parts = append(parts, p)
			}
		}

		if len(parts) < 2 {
			http.NotFound(w, r)
			return
		}

		exType := parts[0]
		exLink := parts[1]

		var example *Example
		for _, ex := range examples {
			if ex.Type == exType && ex.Link == exLink {
				example = &ex
				break
			}
		}

		if example == nil {
			http.NotFound(w, r)
			return
		}

		tmpl.Execute(w, map[string]interface{}{
			"Title": example.Name,
			"Example": example,
		})
	})

	fs := http.FileServer(http.Dir("static"))
	http.Handle("/static/", http.StripPrefix("/static/", fs))

	return http.ListenAndServe(addr, nil)
}

