			if r.Method == "POST" {
				body, err := io.ReadAll(r.Body)
				if err != nil {
					http.Error(w, "Error reading request body", http.StatusInternalServerError)
					return
				}
				ch <- string(body)
				w.Write([]byte("done"))
			} else {
				http.Error(w, "Method not allowed", http.StatusMethodNotAllowed)
			}
		})
		http.ListenAndServe(":"+string(port), nil)
	}()
	return ch
}

