func main() {
	exporter, err := jaeger.NewExporter(jaeger.Options{
		AgentEndpoint:     "localhost:6831",
		CollectorEndpoint: "http://localhost:14268/api/traces",
		Process: jaeger.Process{
			ServiceName: "example-server",
		},
	})
	if err != nil {
		log.Fatal(err)
	}
	trace.RegisterExporter(exporter)
	trace.ApplyConfig(trace.Config{DefaultSampler: trace.AlwaysSample()})

	handler := http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
		ctx, span := trace.StartSpan(r.Context(), "root-handler")
		defer span.End()

		span.AddAttributes(
			trace.StringAttribute("handler", "root"),
			trace.StringAttribute("method", r.Method),
		)

		span.Annotate([]trace.Attribute{
			trace.StringAttribute("annotation", "processing request"),
		}, "Start processing")

		client := &http.Client{
			Transport: &ochttp.Transport{},
		}

		req, err := http.NewRequestWithContext(ctx, "GET", "http://example.com", nil)
		if err != nil {
			http.Error(w, err.Error(), http.StatusInternalServerError)
			return
		}

		resp, err := client.Do(req)
		if err != nil {
			http.Error(w, err.Error(), http.StatusInternalServerError)
			return
		}
		defer resp.Body.Close()

		io.ReadAll(resp.Body)

		span.Annotate([]trace.Attribute{
			trace.StringAttribute("annotation", "request completed"),
		}, "Finished processing")

		w.Write([]byte("hello world"))
	})

	och := &ochttp.Handler{Handler: handler}
	http.Handle("/", och)

	log.Println("Starting server on :50030")
	log.Fatal(http.ListenAndServe(":50030", nil))
