import (
	"context"
	"errors"
	"net/http"
	"sync"
	"time"

	"github.com/go-chi/chi/v5"
)

type Authority struct{}
type Config struct{}
type Server struct {
	http.Server
}
type options struct{}
type TLSRenewer struct{}

type CA struct {
	auth        *Authority
	config      *Config
	srv         *Server
	insecureSrv *Server
	metricsSrv  *Server
	opts        *options
	renewer     *TLSRenewer
	compactStop chan struct{}
}

func (ca *CA) Init(cfg *Config) (*CA, error) {
	ca.config = cfg
	ca.compactStop = make(chan struct{})

	r := chi.NewRouter()
	r.Get("/", func(w http.ResponseWriter, r *http.Request) {
		w.Write([]byte("CA Service"))
	})

	ca.srv = &Server{
		Server: http.Server{
			Addr:    ":443",
			Handler: r,
		},
	}

	if ca.shouldServeInsecureServer() {
		insecureRouter := chi.NewRouter()
		insecureRouter.Get("/health", func(w http.ResponseWriter, r *http.Request) {
			w.Write([]byte("OK"))
		})

		ca.insecureSrv = &Server{
			Server: http.Server{
				Addr:    ":80",
				Handler: insecureRouter,
			},
		}
	}

	return ca, nil
}

func (ca *CA) Run() error {
	var wg sync.WaitGroup
	var errChan = make(chan error, 3)

	wg.Add(1)
	go func() {
		defer wg.Done()
		if err := ca.srv.ListenAndServeTLS("", ""); err != nil && !errors.Is(err, http.ErrServerClosed) {
			errChan <- err
		}
	}()

	if ca.insecureSrv != nil {
		wg.Add(1)
		go func() {
			defer wg.Done()
			if err := ca.insecureSrv.ListenAndServe(); err != nil && !errors.Is(err, http.ErrServerClosed) {
				errChan <- err
			}
		}()
	}

	if ca.metricsSrv != nil {
		wg.Add(1)
		go func() {
			defer wg.Done()
			if err := ca.metricsSrv.ListenAndServe(); err != nil && !errors.Is(err, http.ErrServerClosed) {
				errChan <- err
			}
		}()
	}

	go ca.runCompactJob()

	select {
	case err := <-errChan:
		ca.Stop()
		return err
	default:
		return nil
	}
}

func (ca *CA) shouldServeInsecureServer() bool {
	return true
}

func (ca *CA) runCompactJob() {
	ticker := time.NewTicker(1 * time.Hour)
	defer ticker.Stop()

	for {
		select {
		case <-ticker.C:
		case <-ca.compactStop:
			return
		}
	}
}

func (ca *CA) Stop() error {
	close(ca.compactStop)

	ctx, cancel := context.WithTimeout(context.Background(), 5*time.Second)
	defer cancel()

	var errs []error

	if err := ca.srv.Shutdown(ctx); err != nil {
		errs = append(errs, err)
	}

	if ca.insecureSrv != nil {
		if err := ca.insecureSrv.Shutdown(ctx); err != nil {
			errs = append(errs, err)
		}
	}

	if ca.metricsSrv != nil {
		if err := ca.metricsSrv.Shutdown(ctx); err != nil {
			errs = append(errs, err)
		}
	}

	if len(errs) > 0 {
		return errors.Join(errs...)
	}
	return nil
}

