package main

import (
	"net/http"
	"strings"

	"github.com/alibaba/sentinel-golang/adapter/echo"
	"github.com/labstack/echo/v4"
	"github.com/labstack/echo/v4/middleware"
	sentinel "github.com/alibaba/sentinel-golang/api"
	"github.com/alibaba/sentinel-golang/core/flow"
)

func extractClientIP(next echo.HandlerFunc) echo.HandlerFunc {
	return func(c echo.Context) error {
		realIP := c.Request().Header.Get("X-Real-IP")
		if realIP == "" {
			realIP = c.Request().Header.Get("X-Forwarded-For")
			if realIP != "" {
				realIP = strings.Split(realIP, ",")[0]
			} else {
				realIP = strings.Split(c.Request().RemoteAddr, ":")[0]
			}
		}
		c.Set("clientIP", realIP)
		return next(c)
	}
}

func blockFallback(c echo.Context) error {
	return c.JSON(http.StatusTooManyRequests, map[string]string{
		"message": "too many requests; the quota used up",
	})
}

func setupSentinel() {
	if err := sentinel.InitDefault(); err != nil {
		panic(err)
	}

	_, err := flow.LoadRules([]*flow.Rule{
		{
			Resource:               "GET:/test",
			Threshold:              10,
			TokenCalculateStrategy: flow.Direct,
			ControlBehavior:        flow.Reject,
			StatIntervalInMs:       1000,
		},
	})
	if err != nil {
		panic(err)
	}
}

func main() {
	setupSentinel()

	e := echo.New()
	e.Use(middleware.Logger())
	e.Use(extractClientIP)
	e.Use(sentinelEcho.SentinelMiddlewareWithConfig(
		sentinelEcho.Config{
			BlockFallback: blockFallback,
		},
	))

	e.GET("/test", func(c echo.Context) error {
		return c.JSON(http.StatusOK, map[string]string{
			"message": "test endpoint",
		})
