type Api struct {
	port        int
	bindIp      string
	tlsCertFile string
	tlsKeyFile  string
	timeout     time.Duration
	rootPath    string
	srv         *echo.Echo
}

func (a *Api) Start() error {
	a.srv = echo.New()
	a.srv.HideBanner = true
	a.srv.Server.ReadTimeout = a.timeout
	a.srv.Server.WriteTimeout = a.timeout
	a.srv.Server.IdleTimeout = a.timeout * 2

	a.srv.Use(middleware.Recover())
	a.srv.Use(middleware.Logger())

	a.srv.GET(a.rootPath, func(c echo.Context) error {
		return c.String(http.StatusOK, "OK")
	})

	addr := a.bindIp + ":" + strconv.Itoa(a.port)

	if a.tlsCertFile != "" && a.tlsKeyFile != "" {
		cert, err := tls.LoadX509KeyPair(a.tlsCertFile, a.tlsKeyFile)
		if err != nil {
			return err
		}

		a.srv.TLSServer.TLSConfig = &tls.Config{
			Certificates: []tls.Certificate{cert},
			NextProtos:   []string{"h2", "http/1.1"},
		}

		go func() {
			if err := a.srv.StartTLS(addr, a.tlsCertFile, a.tlsKeyFile); err != nil && err != http.ErrServerClosed {
				a.srv.Logger.Fatal("shutting down the server")
			}
		}()
	} else {
		go func() {
			if err := a.srv.Start(addr); err != nil && err != http.ErrServerClosed {
				a.srv.Logger.Fatal("shutting down the server")
			}
		}()
	}

	return nil
}

func (a *Api) Stop(ctx context.Context) error {
	return a.srv.Shutdown(ctx)
}

