func uploadFile(c *gin.Context) {
	// Parse the multipart form
	form, _ := c.MultipartForm()
	file, _ := form.File["file"]

	// Generate a unique file name
	timestamp := time.Now().Format("20060102150405")
	randomBytes := make([]byte, 6)
	_, err := rand.Read(randomBytes)
	if err != nil {
		c.JSON(500, gin.H{
			"status": 1,
			"message": "Failed to generate random string",
		})
		return
	}
	randomString := fmt.Sprintf("%x", randomBytes)
	extension := filepath.Ext(file.Filename)
	uniqueFileName := fmt.Sprintf("%s-%s%s", timestamp, randomString, extension)

	// Create the file
	dst, err := os.Create(filepath.Join("./root/file/", uniqueFileName))
	if err != nil {
		c.JSON(500, gin.H{
			"status": 1,
			"message": "Failed to create file",
		})
		return
	}
	defer dst.Close()

	// Copy the uploaded file to the destination file
	if _, err := io.Copy(dst, file.Open()); err != nil {
		c.JSON(500, gin.H{
			"status": 1,
			"message": "Failed to save file",
		})
		return
	}

	// Return the URL to the uploaded file
	url := fmt.Sprintf("http://localhost:8085/root/file/%s", uniqueFileName)
	c.JSON(200, gin.H{
		"status": 0,
		"message": "File uploaded successfully",
		"data": gin.H{
			"url": url,
		},
	})
}

