func runTLSHTTPServer(domains []string) {
	g, ctx := errgroup.WithContext(context.Background())

	go func() {
		defer g.Done()
		// Initialize Gin engine with logger and recovery middleware
		r := gin.New()
		r.Use(gin.Logger(), gin.Recovery())

		// Register GET endpoint "/ping" that responds with "pong"
		r.GET("/ping", func(c *gin.Context) {
			c.String(http.StatusOK, "pong")
		})

		// Set up Let's Encrypt client
		client, err := lego.NewClient(&lego.Config{
			CADirURL: "https://acme-v02.api.letsencrypt.org/directory",
			Email:    "admin@example.com",
			Key:      certcrypto.GeneratePrivateKey(certcrypto.RSA2048),
		})
		if err != nil {
			log.Fatal(err)
		}

		// Create a new user
		user, err := client.Registration.Register(registration.RegisterOptions{TermsOfServiceAgreed: true})
		if err != nil {
			log.Fatal(err)
		}

		// Set user
		client.User = user

		// Create a challenge solver for HTTP-01
		solver, err := lego.NewHTTPChallengeSolver()
		if err != nil {
			log.Fatal(err)
		}

		// Obtain a certificate
		certificates, err := client.Certificate.Obtain(certcrypto.PKCS1SHA256, domains, solver)
		if err != nil {
			log.Fatal(err)
		}

		// Set up TLS configuration
		tlsConfig := &tls.Config{
			MinVersion: tls.VersionTLS12,
			Certificates: []tls.Certificate{certificates.Certificate},
		}

		// Start HTTPS server
		server := &http.Server{
			Addr:      ":443",
			Handler:   r,
			TLSConfig: tlsConfig,
		}

		// Start server in a goroutine
		go func() {
			if err := server.ListenAndServeTLS("", ""); err != nil && err != http.ErrServerClosed {
				log.Fatal(err)
			}
		}()

		// Wait for context to be canceled
		<-ctx.Done()
	}()

	// Wait for all goroutines to finish
	if err := g.Wait(); err != nil {
		log.Fatal(err)
	}
