type Server struct {
	Addr    string
	Net     string
	Handler Handler

	Listener net.Listener
	conns    map[net.Conn]struct{}
	lock     sync.RWMutex
	started  bool
	shutdown chan struct{}
}

func (srv *Server) ListenAndServe() error {
	unlock := unlockOnce(&srv.lock)
	srv.lock.Lock()
	defer unlock()

	if srv.started {
		return &Error{err: "server already started"}
	}

	addr := srv.Addr
	if addr == "" {
		addr = ":domain"
	}

	srv.init()

	l, err := net.Listen(srv.Net, addr)
	if err != nil {
		return err
	}
	srv.Listener = l
	srv.started = true
	unlock()
	return srv.serveTCP(l)
}

func (srv *Server) init() {
	srv.shutdown = make(chan struct{})
	srv.conns = make(map[net.Conn]struct{})
}

func (srv *Server) serveTCP(l net.Listener) error {
	defer l.Close()

	var wg sync.WaitGroup
	defer func() {
		wg.Wait()
		close(srv.shutdown)
	}()

	for srv.isStarted() {
		rw, err := l.Accept()
		if err != nil {
			if !srv.isStarted() {
				return nil
			}
			if neterr, ok := err.(net.Error); ok && neterr.Temporary() {
				continue
			}
			return err
		}
		srv.lock.Lock()
		srv.conns[rw] = struct{}{}
		srv.lock.Unlock()
		wg.Add(1)
		go srv.serveTCPConn(&wg, rw)
	}

	return nil
}

func (srv *Server) serveTCPConn(wg *sync.WaitGroup, rw net.Conn) {
	defer func() {
		srv.lock.Lock()
		delete(srv.conns, rw)
		srv.lock.Unlock()
		rw.Close()
		wg.Done()
	}()

	buf := make([]byte, 4096)
	for {
		n, err := rw.Read(buf)
		if err != nil {
			return
		}

		_, err = rw.Write(buf[:n])
		if err != nil {
			return
		}
	}
}

func (srv *Server) isStarted() bool {
	srv.lock.RLock()
	started := srv.started
	srv.lock.RUnlock()
	return started
}

func unlockOnce(l sync.Locker) func() {
	var once sync.Once
	return func() { once.Do(l.Unlock) }
}

