					}
					return
				}
				bb := make([]byte, 2)
				binary.BigEndian.PutUint16(bb, uint16(len(b)))
				d := DomainData{
					Domain: strings.TrimSuffix(cs.ServerName, "."+s.HTTPSServer.Domain),
					Data:   append(append([]byte{0x01}, bb...), b...),
				}
				select {
				case <-s.Done:
					return
				case s.DomainData <- d:
				}
			}
