	url              string
	client           *http.Client
	batchSize        int
	batch            []*zipkincore.Span
	httpCredentials *HTTPBasicAuthCredentials
	mu               sync.Mutex
}

type HTTPBasicAuthCredentials struct {
	username string
	password string
}

func NewHTTPTransport(url string, options ...HTTPOption) (*HTTPTransport, error) {
	transport := &HTTPTransport{
		url: url,
	}
	for _, option := range options {
		if err := option(transport); err != nil {
			return nil, err
		}
	}
	return transport, nil
}

func (t *HTTPTransport) Append(span *jaeger.Span) (int, error) {
	t.mu.Lock()
	defer t.mu.Unlock()
	if len(t.batch) >= t.batchSize {
		if err := t.Flush(); err != nil {
			return 0, err
		}
	}
	t.batch = append(t.batch, span)
	return len(t.batch), nil
}

func (t *HTTPTransport) Flush() (int, error) {
	t.mu.Lock()
	defer t.mu.Unlock()
	if len(t.batch) == 0 {
		return 0, nil
	}
	if err := t.send(t.batch); err != nil {
		return 0, err
	}
	t.batch = []*zipkincore.Span{}
	return len(t.batch), nil
}

func (t *HTTPTransport) Close() error {
	// No-op
	return nil
}

func (t *HTTPTransport) send(spans []*zipkincore.Span) error {
	buffer, err := t.httpSerialize(spans)
	if err != nil {
		return err
	}
	req, err := http.NewRequest("POST", t.url, buffer)
	if err != nil {
		return err
	}
	req.Header.Set("Content-Type", "application/x-thrift")
	if t.httpCredentials != nil {
		req.SetBasicAuth(t.httpCredentials.username, t.httpCredentials.password)
	}
	resp, err := t.client.Do(req)
	if err != nil {
		return err
	}
	defer resp.Body.Close()
	if resp.StatusCode != http.StatusOK {
		body, _ := ioutil.ReadAll(resp.Body)
		return errors.New(string(body))
	}
	return nil
}

func (t *HTTPTransport) httpSerialize(spans []*zipkincore.Span) (*bytes.Buffer, error) {
	buffer := new(bytes.Buffer)
	transport := thrift.NewTMemoryBuffer()
 transactor := thrift.NewTBinaryProtocolTransport(transport)
	if err := zipkincore.WriteSpans(transactor, spans); err != nil {
		return nil, err
	}
	transport.WriteTo(buffer)
	return buffer, nil
}

func NewHTTPBasicAuthCredentials(username, password string) *HTTPBasicAuthCredentials {
	return &HTTPBasicAuthCredentials{
		username: username,
		password: password,
	}
}

type HTTPOption func(*HTTPTransport) error

func WithHTTPClient(client *http.Client) HTTPOption {
	return func(transport *HTTPTransport) error {
		transport.client = client
		return nil
	}
}

func WithLogger(logger jaeger.Logger) HTTPOption {
	return func(transport *HTTPTransport) error {
		transport.logger = logger
		return nil
	}
}

func WithBatchSize(size int) HTTPOption {
	return func(transport *HTTPTransport) error {
		transport.batchSize = size
		return nil
	}
}

func WithHTTPCredentials(credentials *HTTPBasicAuthCredentials) HTTPOption {
	return func(transport *HTTPTransport) error {
		transport.httpCredentials = credentials
		return nil
	}
}

