	return body, nil
}

func (c *Client) testConnection() error {
	for i := 0; i < 20; i++ {
		req, err := http.NewRequest("GET", c.url, nil)
		if err != nil {
			return err
		}

		resp, err := c.httpClient.Do(req)
		if err == nil && resp.StatusCode == http.StatusOK {
