package main

import (
	"bytes"
	"crypto/sha256"
	"encoding/hex"
	"errors"
	"io"
	"net/http"
	"sync"
	"time"
)

type UploadSession struct {
	ID             string
	OldID          string
	ParentID       string
	NodeID         uint64
	Name           string
	ExpirationDateTime time.Time
	Size           uint64
	Data           []byte
	QuickXORHash   string
	ModTime        time.Time
	retries        int
	UploadURL      string
	ETag           string
	state          int
	error          error
	mu             sync.Mutex
}

func (s *UploadSession) uploadChunk(auth *graph.Auth, offset uint64) ([]byte, int, error) {
	s.mu.Lock()
	defer s.mu.Unlock()

	if s.error != nil {
		return nil, 0, s.error
	}

	if offset >= s.Size {
		return nil, 0, errors.New("offset is beyond file size")
	}

	chunkSize := uint64(1024 * 1024) // 1MB chunk size
	end := offset + chunkSize
	if end > s.Size {
		end = s.Size
	}

	chunk := s.Data[offset:end]
	hasher := sha256.New()
	hasher.Write(chunk)
	hash := hex.EncodeToString(hasher.Sum(nil))

	req, err := http.NewRequest("PUT", s.UploadURL, bytes.NewReader(chunk))
	if err != nil {
		return nil, 0, err
	}

	req.Header.Set("Content-Range", fmt.Sprintf("bytes %d-%d/%d", offset, end-1, s.Size))
	req.Header.Set("Content-Type", "application/octet-stream")
	req.Header.Set("Authorization", auth.Token)
	req.Header.Set("X-File-Name", s.Name)
	req.Header.Set("X-File-Size", fmt.Sprintf("%d", s.Size))
	req.Header.Set("X-File-Mod-Time", s.ModTime.Format(time.RFC3339))
	req.Header.Set("X-File-Hash", hash)

	client := &http.Client{}
	resp, err := client.Do(req)
	if err != nil {
		return nil, 0, err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		return nil, resp.StatusCode, fmt.Errorf("server responded with status code: %d", resp.StatusCode)
	}

	body, err := io.ReadAll(resp.Body)
	if err != nil {
		return nil, 0, err
	}

	return body, resp.StatusCode, nil
}
