import (
	"bytes"
	"context"
	"fmt"
	"io"
	"net/url"

	"github.com/gorilla/websocket"
	"golang.org/x/sync/errgroup"
)

type Listener struct {
	URL        *url.URL
	Token      string
	SchemaFunc func([]byte) (io.Reader, error)
	Out        io.Writer
	InCh       <-chan []byte
}

func NewListener(url *url.URL, token string, schemaFunc func([]byte) (io.Reader, error), out io.Writer, inCh <-chan []byte) *Listener {
	return &Listener{
		URL:        url,
		Token:      token,
		SchemaFunc: schemaFunc,
		Out:        out,
		InCh:       inCh,
	}
}

func (l *Listener) Listen(ctx context.Context) error {
	if l.Token != "" {
		params := l.URL.Query()
		params.Set("token", l.Token)
		l.URL.RawQuery = params.Encode()
	}

	c, _, err := websocket.DefaultDialer.Dial(l.URL.String(), nil)
	if err != nil {
		return fmt.Errorf("error creating websocket connection: %w", err)
	}
	defer c.Close()

	done := make(chan struct{})
	grp, ctx := errgroup.WithContext(ctx)
	grp.Go(func() error {
		defer close(done)
		for {
			_, message, err := c.ReadMessage()
			if err != nil {
				if websocket.IsCloseError(err, websocket.CloseNormalClosure) {
					return nil
				}
				return fmt.Errorf("error reading from websocket: %w", err)
			}

			var r io.Reader
			if l.SchemaFunc != nil {
				r, err = l.SchemaFunc(message)
				if err != nil {
					return err
				}
			} else {
				r = bytes.NewReader(message)
			}

			io.Copy(l.Out, r)
		}
	})

	grp.Go(func() error {
		for {
			select {
			case data := <-l.InCh:
				if err := c.WriteMessage(websocket.TextMessage, data); err != nil {
					return fmt.Errorf("error writing to websocket: %w", err)
				}
			case <-ctx.Done():
				if err := c.WriteMessage(websocket.CloseMessage, websocket.FormatCloseMessage(websocket.CloseNormalClosure, "")); err != nil {
					return fmt.Errorf("error writing close message: %w", err)
				}
				return nil
			case <-done:
				return nil
			}
		}
	})
	if err := grp.Wait(); err != nil {
		return err
	}
	return nil
}

