func NewUDPServer(s *Server, p *UDPPacket, addr *net.UDPAddr) (*UDPServer, error) {
	bye := func(err error) {
		p := &UDPPacket{
			Address: err.Error(),
		}
		b, err1 := proto.Marshal(p)
		if err1 != nil {
