func newlogRetriever(logFile string) (l *logRetriever, err error) {
	l = &logRetriever{}
	if l.reader, l.writer, err = os.Pipe(); err != nil {
		return
	}

	if l.output, err = newFileCloner(l.writer, logFile); err != nil {
		return
	}

