func webhookHandler(w http.ResponseWriter, r *http.Request) {
	if r.Method != "POST" {
		http.Error(w, "Method not allowed", http.StatusMethodNotAllowed)
		return
	}

	body, err := ioutil.ReadAll(r.Body)
	if err != nil {
		http.Error(w, "Error reading request body", http.StatusInternalServerError)
		return
	}
	defer r.Body.Close()

	signature := r.Header.Get("Stripe-Signature")
	apiKey := os.Getenv("apiKey")
	webhookSecret := os.Getenv("webhookSecret")

	if !validateSignature(body, signature, webhookSecret) {
		http.Error(w, "Invalid signature", http.StatusUnauthorized)
		return
	}

	var event map[string]interface{}
	if err := json.Unmarshal(body, &event); err != nil {
		http.Error(w, "Invalid JSON payload", http.StatusInternalServerError)
		return
	}

	switch event["type"] {
	case "v1.billing.meter.error_report_triggered":
		handleBillingMeterErrorReport(event)
	default:
		fmt.Printf("Unhandled event type: %s\n", event["type"])
	}

	w.WriteHeader(http.StatusOK)
}

func validateSignature(body []byte, signature, secret string) bool {
	signatureParts := strings.Split(signature, "=")
	if len(signatureParts) != 2 {
		return false
	}

	signatureHash := hex.EncodeToString(sha256.NewHMAC([]byte(secret), []byte(signatureParts[1])).Sum(body))
	return signatureHash == signatureParts[1]
}

func handleBillingMeterErrorReport(event map[string]interface{}) {
	meterID := event["data"].(map[string]interface{})["object"].(map[string]interface{})["id"].(string)
	fmt.Printf("Handled billing meter error report for meter ID: %s\n", meterID)
}

