	nonce := fmt.Sprintf("%x", crypto/rand.Reader.Read(make([]byte, 16)))

	signature := fmt.Sprintf("AWS4-HMAC-SHA256 Credential=%s/%s, SignedHeaders=host;x-amz-date, Signature=%s", accessKey, timestamp, calculateSignature(secretKey, timestamp, "host", "x-amz-date", nonce))
