package main

import (
	"net/http"
	"sync"

	"github.com/labstack/echo/v4"
	"github.com/labstack/echo/v4/middleware"
	"github.com/yosssi/melody"
	"github.com/golang/go/src/container/ring"
	"github.com/yourorg/bridge"
)

type API struct {
	Messages *ring.Ring
	RWMutex  sync.RWMutex
	Config   *bridge.Config
	mrouter  *melody.Melody
}

func New(cfg *bridge.Config) bridge.Bridger {
	api := &API{
		Messages: ring.New(cfg.MessageCapacity),
		Config:   cfg,
		mrouter:  melody.New(),
	}

	e := echo.New()
	e.Use(middleware.Logger())
	e.Use(middleware.Recover())

	api.mrouter.HandleMessage(api.handleWebsocketMessage)
	e.GET("/ws", api.handleWebsocket)
	e.GET("/health", api.handleHealthcheck)
	e.GET("/messages", api.handleMessages)
	e.GET("/stream", api.handleStream)
	e.POST("/message", api.handlePostMessage)

	go func() {
		if err := e.Start(cfg.Address); err != nil && err != http.ErrServerClosed {
			e.Logger.Fatal("shutting down the server")
		}
	}()

	return api
}

func (api *API) handleWebsocketMessage(message []byte, s *melody.Session) {
	api.RWMutex.Lock()
	defer api.RWMutex.Unlock()

	api.Messages.Value = message
	api.Messages = api.Messages.Next()

	api.mrouter.Broadcast(message)
}

func (api *API) handleWebsocket(c echo.Context) error {
	return api.mrouter.HandleRequest(c.Response(), c.Request())
}

func (api *API) handleHealthcheck(c echo.Context) error {
	return c.String(http.StatusOK, "OK")
}

func (api *API) handleMessages(c echo.Context) error {
	api.RWMutex.RLock()
	defer api.RWMutex.RUnlock()

	var messages []string
	api.Messages.Do(func(p interface{}) {
		if p != nil {
			messages = append(messages, string(p.([]byte)))
		}
	})

	return c.JSON(http.StatusOK, messages)
}

func (api *API) handleStream(c echo.Context) error {
	return api.mrouter.HandleRequest(c.Response(), c.Request())
}

func (api *API) handlePostMessage(c echo.Context) error {
	msg := new(bridge.Message)
	if err := c.Bind(msg); err != nil {
		return err
	}

	api.RWMutex.Lock()
	defer api.RWMutex.Unlock()

	api.Messages.Value = msg.Content
	api.Messages = api.Messages.Next()

	api.mrouter.Broadcast(msg.Content)

	return c.NoContent(http.StatusCreated)
}

func main() {
	cfg := &bridge.Config{
		Address:        ":8080",
		MessageCapacity: 100,
	}
	api := New(cfg)
	_ = api
}
