package main

import (
	"net/http"
	"github.com/labstack/echo/v4"
	"github.com/labstack/echo/v4/middleware"
)

type Host struct {
	Echo *echo.Echo
}

func startReverseProxyServer() {
	hosts := map[string]*Host{
		"example.com": &Host{Echo: echo.New()},
		"test.com":    &Host{Echo: echo.New()},
	}

	for _, host := range hosts {
		host.Echo.Use(middleware.Logger())
		host.Echo.GET("/", func(c echo.Context) error {
			return c.String(http.StatusOK, "Hello, World!")
		})
	}

	e := echo.New()
	e.Any("/*", func(c echo.Context) (err error) {
		hostHeader := c.Request().Host
		if host, ok := hosts[hostHeader]; ok {
			host.Echo.ServeHTTP(c.Response(), c.Request())
		} else {
			return echo.NewHTTPError(http.StatusNotFound, "Host not found")
		}
		return
	})

	e.Start(":8080")
}

func main() {
	startReverseProxyServer()
}
