package main

import (
	"fmt"
	"net/http"
	"github.com/labstack/echo/v4"
	"github.com/labstack/echo/v4/middleware"
)

var (
	workflowKey string
	builderKey string
	secretsHit bool
	triggerHit bool
)

func startWorkflowServer(host string, port uint64) {
	e := echo.New()
	e.Use(middleware.Logger())
	e.Use(middleware.Recover())

	e.Use(func(next echo.HandlerFunc) echo.HandlerFunc {
		return func(c echo.Context) error {
			key := c.Request().Header.Get("X-Workflow-Key")
			if key != workflowKey {
				return echo.NewHTTPError(http.StatusUnauthorized, "Invalid workflow key")
			}
			return next(c)
		}
	})

	e.GET("/secrets", func(c echo.Context) error {
		secretsHit = true
		return c.String(http.StatusOK, "Secrets accessed")
	})

	e.POST("/trigger", func(c echo.Context) error {
		triggerHit = true
		return c.String(http.StatusOK, "Triggered workflow")
	})

	address := fmt.Sprintf("%s:%d", host, port)
	e.Start(address)
}

func main() {
	startWorkflowServer("localhost", 8080)
}

